// Realistic Opossum - Medium Complexity
// Size: ~80mm long, printable without supports

// Overall dimensions
body_length = 70;
body_height = 30;
body_width = 25;

// Head dimensions
head_length = 25;
head_height = 20;
head_width = 20;

$fn = 32;

// Main assembly
union() {
    // Body
    translate([0, 0, body_height/2])
        scale([1, 0.8, 0.6])
        sphere(d=body_width);

    // Torso elongation
    translate([-body_length/4, 0, body_height/3])
        scale([1.5, 1, 0.8])
        sphere(d=body_width);

    // Head
    translate([body_length/2 + 5, 0, body_height/2 + 5])
        rotate([0, -10, 0])
        union() {
            // Main head sphere
            scale([1.2, 1, 1])
            sphere(d=head_width);

            // Snout
            translate([head_length/2, 0, -head_height/4])
                scale([0.8, 0.6, 0.6])
                sphere(d=head_width/2);

            // Ears
            for(side = [-1, 1]) {
                translate([head_length/4, side*head_width/2, head_height/2])
                    rotate([0, 30*side, 0])
                    scale([0.5, 0.3, 1])
                    cylinder(h=head_height/2, d1=head_width/3, d2=head_width/4);
            }

            // Eyes
            for(side = [-1, 1]) {
                translate([head_length/2 - 3, side*head_width/3, head_height/4])
                    sphere(d=3);
                translate([head_length/2 - 3, side*head_width/3, head_height/4])
                    sphere(d=1.5);
            }

            // Nose
            translate([head_length/2 + 5, 0, -head_height/4])
                sphere(d=2);
        }

    // Limbs
    module limb(length, diameter) {
        union() {
            cylinder(h=length, d=diameter);
            translate([0, 0, length])
                sphere(d=diameter);
        }
    }

    // Front legs
    for(side = [-1, 1]) {
        translate([body_length/4, side*body_width/2, 0])
            rotate([0, side*15, 0])
            limb(20, 6);
    }

    // Rear legs
    for(side = [-1, 1]) {
        translate([-body_length/3, side*body_width/2, 0])
            rotate([0, side*15, -10])
            limb(25, 8);
    }

    // Tail
    translate([-body_length/2 - 10, 0, body_height/3])
        rotate([0, -30, 0])
        scale([1, 1, 0.4])
        cylinder(h=40, d1=8, d2=4);

    // Whiskers
    for(i = [0:2]) {
        for(side = [-1, 1]) {
            translate([body_length/2 + 10, side*2, 8 - i*2])
                rotate([0, 90, side*15*(i+1)])
                cylinder(h=10, d=0.5);
        }
    }

    // Fur texture with bumps
    for(i = [0:10]) {
        for(j = [0:5]) {
            translate([
                body_length/2 - i*body_length/10 + rand(-2,2),
                rand(-body_width/2, body_width/2),
                rand(body_height/4, body_height*3/4)
            ])
                sphere(d=1.5);
        }
    }
}

// Helper for random values within range
function rand(min, max) = min + (max - min) * (i*13 % 100) / 100;

