// Oarfish 3D Model
body_length = 200; // Overall body length
body_width = 10;   // Maximum body width
head_size = 15;    // Head diameter

module oarfish() {
    // Main body - long tapered cylinder
    color("Silver") {
        // Head section (spherical)
        translate([0, 0, head_size/2]) 
        sphere(d=head_size, $fn=32);

        // Tapered body
        translate([0, 0, head_size]) 
        cylinder(
            h=body_length - head_size, 
            d1=body_width, 
            d2=body_width/3, 
            $fn=32
        );
    }

    // Dorsal fin - long ridge along body
    color("Crimson") {
        for(z = [head_size : 10 : body_length - 15]) {
            translate([0, body_width/2 * 0.8, z]) 
            rotate([90, 0, 0]) 
            scale([1, 0.1 + z/body_length, 1]) 
            cylinder(h=5, d=5, center=true, $fn=3);
        }
    }

    // Tail fin - forked structure
    color("Crimson") {
        translate([0, 0, body_length]) {
            // Upper tail fork
            rotate([-30, 0, 0]) 
            cylinder(h=25, d1=3, d2=0, $fn=16);

            // Lower tail fork
            rotate([30, 0, 0]) 
            cylinder(h=25, d1=3, d2=0, $fn=16);
        }
    }

    // Pectoral fins - small side fins near head
    color("Crimson") {
        for(side = [-1, 1]) {
            translate([side * body_width/2, 0, head_size * 1.5]) 
            rotate([0, 90, side * 90]) 
            scale([1, 0.5, 1]) 
            cylinder(h=5, d=8, center=true, $fn=3);
        }
    }

    // Eyes
    color("Black") {
        for(eye = [-1, 1]) {
            translate([eye * head_size/3, head_size/3, head_size * 0.7]) 
            sphere(d=2, $fn=12);
        }
    }
}

// Position for printing/viewing
rotate([90, 0, 0])  // Lay flat for better viewing
oarfish();

