// OpenSCAD Oarfish Model (Medium Complexity)
// Realistic style, 3D printable

// Overall dimensions
length = 100;
height = 20;
thickness = 8;

// Body parameters
segments = 12;
body_taper = 0.3;

// Head parameters
head_length = 15;
head_height = 12;
head_width = 10;

// Dorsal fin parameters
fin_height = 8;
fin_base_width = 3;
fin_segments = 20;

// Eye parameters
eye_radius = 1.5;
eye_offset = 5;

// Mouth parameters
mouth_width = 4;
mouth_height = 2;

// Color definitions
body_color = "silver";
fin_color = "red";

// Main body module
module oarfish_body() {
    union() {
        // Head
        translate([-length/2, 0, 0])
        scale([1.2, 1, 0.8])
        sphere(r=head_height/2);

        // Main body segments
        for(i = [0:segments-1]) {
            t = i / (segments - 1);
            x = -length/2 + head_length + t * (length - head_length);
            y_scale = 1 - t * (1 - body_taper);
            z_scale = 1 - t * (1 - body_taper);

            translate([x, 0, 0])
            scale([1, y_scale, z_scale])
            sphere(r=height/2, $fn=16);
        }

        // Tail taper
        translate([length/2 - 5, 0, 0])
        scale([0.5, body_taper, body_taper])
        sphere(r=height/2, $fn=16);
    }
}

// Dorsal fin module
module dorsal_fin() {
    color(fin_color)
    for(i = [0:fin_segments-1]) {
        t = i / (fin_segments - 1);
        x = -length/2 + head_length + t * (length - head_length - 5);
        height_scale = sin(t * 180) * 0.8 + 0.2;

        if(i % 2 == 0) {
            translate([x, 0, height/2])
            rotate([0, 0, 90])
            scale([height_scale, 1, 1])
            cylinder(h=fin_height * height_scale, r1=fin_base_width/2, r2=0.5, $fn=8);
        }
    }
}

// Eye module
module eye() {
    color("black")
    translate([-length/2 + head_length/2, head_height/2 - eye_offset, head_width/2 - 1])
    sphere(r=eye_radius, $fn=16);

    mirror([0, 1, 0])
