// Oryx - Realistic Style, Medium Complexity
// Basic OpenSCAD primitives only

$fn = 30;
wall = 1.6;

// Main body
module body() {
    // Torso
    hull() {
        translate([0, 0, 30]) sphere([15, 10, 12]);
        translate([0, 0, 15]) sphere([10, 8, 10]);
    }

    // Rump
    translate([-20, 0, 25]) scale([1.2, 1, 1]) sphere([12, 10, 10]);

    // Chest
    translate([15, 0, 20]) sphere([10, 8, 12]);
}

// Leg module
module leg(height, angle, offset) {
    rotate([0, 0, angle]) translate(offset) {
        // Upper leg
        cylinder(h=height/2, r1=4, r2=3, center=true);
        translate([0, 0, -height/4]) rotate([90, 0, 0]) 
            cylinder(h=height/2, r1=3, r2=2.5, center=true);

        // Lower leg
        translate([0, 0, -height/2]) cylinder(h=height/2, r1=2.5, r2=2, center=true);

        // Hoof
        translate([0, 0, -height*0.75]) cylinder(h=4, r1=2, r2=3, center=true);
    }
}

// Head with horns
module head() {
    // Head base
    translate([25, 0, 35]) rotate([0, -20, 0]) {
        hull() {
            sphere([8, 6, 6]);
            translate([12, 0, 0]) sphere([4, 4, 4]);
        }

        // Snout
        translate([12, 0, -2]) scale([2, 0.8, 0.8]) sphere([4, 3, 3]);

        // Horns
        for(i = [-1, 1]) {
            translate([5, i*3, 6]) rotate([20, i*15, 0]) {
                cylinder(h=15, r1=1.5, r2=0.5);
                translate([0, 0, 15]) sphere([0.5, 0.5, 0.5]);
            }
        }

        // Ears
        translate([2, i*4, 4]) rotate([0, 30, 0]) 
            scale([0.3, 1, 2]) sphere([3, 2, 2]);
    }
}

// Tail
module tail() {
    translate([-25, 0, 25]) rotate([45, 0, 0]) {
        cylinder(h=15, r1=2, r2=1);
        translate([0, 0, 15]) scale([0.5, 0.5, 1]) sphere([4, 4, 4]);
    }
}

