// Opah (Lampris guttatus) - Realistic medium-complexity model
// 3D printable, ~75 mm length, minimal overhangs

$fn = 32;
body_length = 75;
body_height = 35;
body_width = 12;

module opah_body() {
    // Main oval body
    union() {
        // Central torso
        translate([0, 0, 0])
            scale([1, 0.6, 0.4])
            sphere(d = body_length * 0.7);

        // Tail extension
        translate([-body_length * 0.45, 0, 0])
            rotate([0, 0, 0])
            scale([0.5, 0.3, 0.15])
            sphere(d = body_length * 0.5);

        // Head dome
        translate([body_length * 0.3, 0, body_height * 0.1])
            scale([0.7, 0.6, 0.5])
            sphere(d = body_length * 0.4);
    }
}

module opah_fins() {
    // Dorsal fin - tall and curved
    translate([0, 0, body_height * 0.3])
        rotate([90, 0, 0])
        linear_extrude(height = body_width * 0.3)
        polygon(points = [
            [0, 0],
            [-body_length * 0.1, body_height * 0.3],
            [-body_length * 0.2, body_height * 0.35],
            [-body_length * 0.3, body_height * 0.3],
            [-body_length * 0.4, body_height * 0.15]
        ]);

    // Pectoral fins - wing-like
    for (side = [-1, 1]) {
        translate([body_length * 0.2, side * body_width * 0.7, -body_height * 0.1])
            rotate([0, -20 * side, 20 * side])
            scale([1, 0.2, 0.05])
            sphere(d = body_length * 0.3);
    }

    // Pelvic fins
    for (side = [-1, 1]) {
        translate([-body_length * 0.1, side * body_width * 0.6, -body_height * 0.2])
            rotate([0, 30, 0])
            scale([0.3, 0.1, 0.02])
            sphere(d = body_length * 0.2);
    }

    // Caudal fin - crescent shape
    translate([-body_length * 0.5, 0, 0])
        rotate([0, 90, 0])
        scale([1, 0.3, 0.05])
        sphere(d = body_length * 0.4);
}

module opah_eyes() {
    for (side = [-1, 1]) {
        // Eye socket
        translate([body_length * 0.35, side * body_width * 0.25, body_height * 0.15])
            sphere(d = 4);

        // Eye ball
        translate([body_length * 0.36, side * body_width * 0.26, body_height * 0.15])
            sphere(d = 2.5);

        // Pupil
        translate([body_length * 0.37, side * body_width * 0.27, body_height * 0.15])
            sphere(d = 1);
    }
}

module opah_mouth() {
    // Mouth cavity
    translate([body_length * 0.4, 0, -body_height * 0.1])
        rotate([0, 0, 0])
        scale([0.3, 0.4, 0.1])
        sphere(d = body_length * 0.2);
}

module opah_details() {
    // Scale texture using small spheres
    for (i = [-3:3]) {
        for (j = [-2:2]) {
            if (i*i + j*j < 9) {
                translate([i * 6, j * 5, body_height * 0.4])
                    sphere(d = 2);
            }
        }
    }

    // Lateral line
    hull() {
        translate([body_length * 0.3, 0, 0])
            sphere(d = 1.5);
        translate([-body_length * 0.4, 0, 0])
            sphere(d = 1.5);
    }
}

// Main assembly
union() {
    // Body with wall thickness for printing
    difference() {
        opah_body();
        scale([0.9, 0.9, 0.9])
            opah_body();
    }

    // Fins
    opah_fins();

    // Eyes
    opah_eyes();

    // Mouth cavity
    difference() {
        opah_body();
        opah_mouth();
    }

    // Surface details
    opah_details();
}

