// 3D printable olive with pit and surface details

// Main parameters
olive_length = 40;
olive_width = 28;
stem_length = 8;
stem_width = 3;
wall_thickness = 2;
surface_detail = 0.4;

// Create the olive body
module olive_body() {
    difference() {
        // Main olive shape - ellipsoid
        scale([1, 0.7, 0.9])
        sphere(olive_width/2, $fn=64);

        // Subtle indentations for realistic surface
        for (i = [0:7]) {
            rotate([0, 0, i * 45])
            translate([olive_width/2 - 2, 0, 0])
            scale([1, 0.8, 0.5])
            sphere(surface_detail * 3, $fn=16);
        }

        // Hollow out the interior
        scale([1, 0.7, 0.9])
        sphere(olive_width/2 - wall_thickness, $fn=64);
    }
}

// Create the olive pit
module olive_pit() {
    difference() {
        // Main pit shape
        scale([0.8, 0.6, 0.75])
        sphere(olive_width/2 - wall_thickness - 2, $fn=32);

        // Add texture to pit
        for (i = [0:11]) {
            rotate([0, 0, i * 30])
            translate([olive_width/3 - 3, 0, 0])
            scale([0.5, 0.3, 0.3])
            sphere(1.5, $fn=8);
        }
    }
}

// Create the stem
module olive_stem() {
    union() {
        // Main stem
        translate([0, 0, olive_width/2 * 0.9])
        rotate([90, 0, 0])
        cylinder(h=stem_length, r=stem_width/2, $fn=16);

        // Stem tip
        translate([0, 0, olive_width/2 * 0.9 + stem_length])
        rotate([90, 0, 0])
        cylinder(h=3, r1=stem_width/2, r2=0, $fn=16);
    }
}

// Small bumps on olive surface
module surface_bumps() {
    for (i = [0:20]) {
        rotate([0, i * 137.5, i * 73.5])
        translate([olive_width/2 - 1.5, 0, 0])
        sphere(surface_detail, $fn=8);
    }
}

// Assembly of the complete olive
union() {
    // Olive body
    olive_body();

    // Olive pit inside
    translate([0, 0, -2])
    olive_pit();

    // Stem
    olive_stem();

    // Surface details
    surface_bumps();
}

