// Size: 80mm nose to tail, 25mm height

$fn = 32;

// Main otter module
module otter() {
    // Body
    union() {
        // Torso
        translate([0, 0, 8])
            scale([1.5, 1, 1])
            sphere(12);

        // Neck
        translate([-18, 0, 12])
            rotate([0, 15, 0])
            cylinder(h=12, r1=8, r2=6);

        // Head
        translate([-32, 0, 18])
            scale([1.1, 0.9, 0.9])
            sphere(10);

        // Tail
        translate([18, 0, 8])
            rotate([0, -30, 0])
            scale([3, 1, 0.6])
            cylinder(h=25, r1=6, r2=3);

        // Legs
        for(side = [-1, 1]) {
            // Front legs
            translate([-20, side*8, 5])
                rotate([30, 0, side*10])
                cylinder(h=12, r1=4, r2=3);

            // Back legs
            translate([15, side*8, 4])
                rotate([40, 0, side*(-10)])
                cylinder(h=14, r1=4.5, r2=3);
        }

        // Ears
        for(side = [-1, 1]) {
            translate([-36, side*8, 22])
                rotate([0, 0, side*30])
                scale([1, 0.7, 1])
                cylinder(h=6, r1=4, r2=0);
        }

        // Snout
        translate([-40, 0, 16])
            scale([1.2, 0.8, 0.8])
            sphere(6);

        // Whiskers bases
        for(side = [-1, 1]) {
            for(pos = [0, 3, 6]) {
                translate([-42, side*(4+pos*0.5), 15])
                    sphere(1);
            }
        }

        // Feet webbing
        for(side = [-1, 1]) {
            // Front feet
            translate([-28, side*11, 0])
                rotate([90, 0, 0])
                cylinder(h=2, r1=5, r2=0);

            // Back feet
            translate([12, side*11, 0])
                rotate([90, 0, 0])
                cylinder(h=2, r1=5.5, r2=0);
        }
    }

    // Eyes (cutouts)
    difference() {
        union() {}
        for(side = [-1, 1]) {
            translate([-38, side*5, 19])
                sphere(2.5);
        }
    }

    // Nose
    translate([-44, 0, 16])
        rotate([90, 0, 0])
        cylinder(h=2, r1=3, r2=0);
}

// Main assembly
otter();

