$fn = 32;

// Main module
module ostrich() {
    body();
    neck();
    head();
    legs();
    tail();
    wings();
}

// Body - oval torso
module body() {
    difference() {
        scale([1.2, 0.8, 1]) sphere(r = 15);
        translate([0, 0, -15]) cube([50, 50, 30], center = true);
    }
}

// Neck - tapered cylinder segments
module neck() {
    for(i = [0:8]) {
        translate([5 + i*2, 0, 12 + i*1.5]) {
            rotate([0, 15, 0]) 
                scale([1 - i*0.08, 1 - i*0.08, 0.8]) 
                    sphere(r = 3.5 - i*0.2);
        }
    }
}

// Head - beak and eyes
module head() {
    translate([22, 0, 26]) {
        rotate([0, -15, 0]) {
            // Head
            scale([1.3, 0.9, 1.1]) sphere(r = 4);

            // Beak
            translate([3.5, 0, -1]) {
                rotate([0, -30, 0]) 
                    scale([2, 0.6, 0.6]) 
                        cylinder(h = 6, r1 = 2, r2 = 1);
            }

            // Eyes
            for(side = [-1, 1]) {
                translate([1, side*2, 1]) 
                    sphere(r = 0.8);
                translate([1.5, side*2, 1]) 
                    scale([0.5, 0.5, 0.2]) 
                        sphere(r = 0.8);
            }
        }
    }
}

// Legs with joints
module legs() {
    for(side = [-1, 1]) {
        translate([0, side*6, -10]) {
            // Upper leg
            rotate([90, 0, 0]) 
                cylinder(h = 15, r = 2.5);

            // Knee joint
            translate([0, 0, -15]) {
                sphere(r = 3);

                // Lower leg
                rotate([90, 0, side*5]) 
                    cylinder(h = 12, r = 2);

                // Foot
                translate([0, 0, -12]) {
                    rotate([90, 0, 0]) {
                        scale([2, 1, 0.5]) 
                            sphere(r = 3);
                        for(toe = [-1, 0, 1]) {
                            translate([toe*2.5, 0, -1]) 
                                scale([0.4, 0.4, 2]) 
                                    cylinder(h = 4, r = 1);
                        }
                    }
                }
            }
        }
    }
}

// Tail - feather group
module tail() {
    translate([-15, 0, -5]) {
        for(i = [0:7]) {
            rotate([0, -20 - i*5, i*10 - 35]) {
                translate([0, 0, -i*0.5]) {
                    scale([1.5, 0.2, 0.8]) 
                        cylinder(h = 8 + i*0.5, r1 = 3 - i*0.2, r2 = 1);
                }
            }
        }
    }
}

