// Owlfly - Realistic style, medium complexity
// Wingspan approximately 80mm, body length ~50mm

$fn = 24;

module thorax() {
    // Main thorax body
    difference() {
        scale([1.2, 1, 0.8]) sphere(d=20);
        // Neck cavity
        translate([0, 12, 0]) scale([0.6, 0.6, 0.6]) sphere(d=15);
    }

    // Prothorax segment
    translate([0, 8, 0]) scale([1, 0.9, 0.7]) sphere(d=18);

    // Pronotum extension
    translate([0, 6, 3]) rotate([30,0,0]) cube([12, 8, 4], center=true);
}

module abdomen() {
    // Tapered abdomen segments
    for(i = [0:5]) {
        hull() {
            translate([0, -8 - i*5, 0]) 
                scale([1 - i*0.1, 1 - i*0.08, 0.8 - i*0.08]) 
                sphere(d=16 - i*2);
            translate([0, -12 - i*5, 0]) 
                scale([1 - (i+1)*0.1, 1 - (i+1)*0.08, 0.8 - (i+1)*0.08]) 
                sphere(d=16 - (i+1)*2);
        }
    }
}

module head() {
    // Main head sphere
    translate([0, 18, 0]) scale([1.1, 1.3, 0.9]) sphere(d=15);

    // Compound eyes
    for(side = [-1, 1]) {
        translate([side*7, 17, 3]) sphere(d=6);
        translate([side*6, 19, 3]) sphere(d=5);
    }

    // Mandibles
    for(side = [-1, 1]) {
        translate([side*3, 22, -2]) rotate([0,0,side*20]) 
            scale([0.8, 2, 0.6]) cylinder(d1=3, d2=2, h=5);
    }

    // Antennae
    for(side = [-1, 1]) {
        translate([side*4, 20, 5]) rotate([0,side*20,0]) 
            cylinder(d1=1.5, d2=0.5, h=12);
    }
}

module wing() {
    // Main wing structure
    difference() {
        linear_extrude(height=0.8) 
            polygon(points=[
                [0,0], [25,0], [35,8], [30,25], [15,35], [5,30], [0,15]
            ]);

        // Wing veins as negative space
        for(i = [1:4]) {
            rotate([0,0,-15 + i*10]) 
                translate([0,0,-0.5]) cube([30, 0.4, 2]);
        }
    }

    // Wing base joint
    translate([0,0,-0.5]) cylinder(h=2, d=4);
}

module leg() {
    // Coxa
    rotate([0,0,30]) cylinder(h=5, d=3);

    // Femur
    translate([0,0,5]) rotate([0,0,-30]) cylinder(h=8, d=2.5);

    // Tibia
    translate([0,0,13]) rotate([0,0,-45]) cylinder(h=10, d=2);

    // Tarsus segments
    for(i = [0:3]) {
        translate([0,0,23 + i*2]) rotate([0,0,-60 + i*5]) 
            cylinder(h=2, d=2 - i*0.3);
    }
}

// Assembly
union() {
    // Body parts
    color("darkgreen") thorax();
    color("darkgreen") translate([0,-35,0]) abdomen();
    color("darkolivegreen") head();

    // Wings - positioned at thorax
    for(side = [-1, 1]) {
        color("transparent") translate([side*8, 5, 5]) rotate([0,0,side*30]) wing();
    }

    // Legs - 6 legs, 3 per side
    for(side = [-1, 1]) {
        for(pos = [0, 4, 8]) {
            color("black") 
                translate([side*10, 8 - pos, -2]) 
                rotate([0,90,side*-20]) leg();
        }
    }
}

