// Realistic 3D-printable oyster with medium complexity
// Overall size: ~80 mm across (fits most FDM beds)

$fn = 36;            // Smooth curves
shell_thk = 2;       // Printable wall thickness

// Main module
module oyster() {
    difference() {
        union() {
            // Bottom shell (cupped)
            translate([0, 0, -10])
                scale([1, 0.85, 0.6])
                sphere(d = 80);

            // Top shell (flatter)
            translate([0, 0, 10])
                rotate([15, 0, 0])
                scale([1, 0.9, 0.4])
                sphere(d = 78);

            // Hinge bulge
            translate([0, 38, 0])
                rotate([90, 0, 0])
                cylinder(h = 25, d1 = 22, d2 = 18, $fn = 32);
        }

        // Hollow out bottom shell
        translate([0, 0, -8])
            scale([1, 0.85, 0.6])
            sphere(d = 80 - 2*shell_thk);

        // Hollow out top shell
        translate([0, 0, 10])
            rotate([15, 0, 0])
            scale([1, 0.9, 0.4])
            sphere(d = 78 - 2*shell_thk);

        // Hollow hinge
        translate([0, 38, 0])
            rotate([90, 0, 0])
            cylinder(h = 25, d1 = 22 - 2*shell_thk, d2 = 18 - 2*shell_thk, $fn = 32);

        // Opening gap between shells
        translate([0, 0, -20])
            cube([120, 120, 40], center = true);
    }

    // Radial shell ridges on bottom
    for (a = [0 : 20 : 340]) {
        rotate([0, 0, a])
            translate([0, 0, -9.5])
                linear_extrude(height = 2, scale = 1.05)
                    offset(delta = 0.5)
                    projection()
                        translate([0, -15, 0])
                            scale([1, 0.85, 1])
                                circle(d = 70);
    }

    // Growth rings on top shell
    for (r = [25 : 7 : 38]) {
        translate([0, 0, 10])
            rotate([15, 0, 0])
                scale([1, 0.9, 0.4])
                    difference() {
                        sphere(d = r*2 + 1);
                        sphere(d = r*2 - 1);
                    }
    }

    // Adductor muscle scar (flat disk)
    translate([0, -12, -5])
        rotate([0, 0, 0])
            cylinder(h = 0.5, d = 18, $fn = 32);

    // Simple gill ridges inside bottom shell
    for (y = [-20 : 6 : 20]) {
        translate([0, y, -7])
            rotate([90, 0, 0])
                cylinder(h = 0.8, d = 60 - abs(y)*1.5, $fn = 32);
    }
}

// Render the model
oyster();

