// Partridge - Realistic style, medium complexity
// Designed for 3D printing with no overhangs

// Parameters
body_length = 40;
body_height = 30;
body_width = 25;
head_size = 12;
beak_length = 8;
leg_height = 15;
tail_length = 25;
wing_span = 30;

// Main module
module partridge() {
    union() {
        // Body
        body();

        // Head and beak
        translate([body_length/2 - 5, 0, body_height/2 + 5])
            head();

        // Wings
        translate([-body_length/4, 0, body_height/2])
            left_wing();

        mirror([0,1,0])
            translate([-body_length/4, 0, body_height/2])
                right_wing();

        // Legs
        translate([0, -body_width/3, -body_height/2])
            left_leg();

        translate([0, body_width/3, -body_height/2])
            right_leg();

        // Tail
        translate([-body_length/2 - 5, 0, body_height/4])
            tail();
    }
}

module body() {
    // Main body - oval shape
    scale([1.5, 1, 1])
        sphere(d=body_width, $fn=30);

    // Chest bulge
    translate([body_length/3, 0, -body_height/4])
        sphere(d=body_width * 0.8, $fn=20);
}

module head() {
    // Head sphere
    sphere(d=head_size, $fn=20);

    // Beak
    translate([head_size/2 + beak_length/2, 0, -head_size/4])
        rotate([0, -20, 0])
            scale([2, 0.5, 0.5])
                cylinder(h=beak_length, d1=head_size/2, d2=head_size/4, $fn=8);

    // Eye
    translate([head_size/3, head_size/3, head_size/4])
        sphere(d=2, $fn=10);
}

module left_wing() {
    // Wing base
    rotate([0, 30, 45])
        scale([1.2, 0.3, 1])
            sphere(d=wing_span/2, $fn=15);

    // Wing feathers
    for(i = [0:2]) {
        rotate([0, 30 - i*10, 45 + i*5])
            translate([wing_span/4 - i*3, 0, -i*2])
                scale([1 - i*0.2, 0.2, 0.8])
                    sphere(d=wing_span/3, $fn=8);
    }
}

module right_wing() {
    left_wing();
}

module left_leg() {
    // Upper leg
    cylinder(h=leg_height/2, d=4, $fn=8);

    // Lower leg
    translate([0, 0, -leg_height/2])
        cylinder(h=leg_height/2, d=3, $fn=8);

    // Foot
    translate([2, 0, -leg_height])
        union() {
            cube([6, 2, 1]);
            translate([2, -2, 0])
                cube([2, 4, 1]);
            translate([2, 2, 0])
                cube([2, 4, 1]);
        }
}

module right_leg() {
    left_leg();
}

module tail() {
    // Tail base
    rotate([0, -20, 0])
        scale([0.5, 1, 0.3])
            sphere(d=tail_length, $fn=15);

    // Tail feathers
    for(i = [-2:2]) {
        rotate([0, -20 - i*5, i*10])
            translate([-tail_length/2, i*3, 0])
                scale([0.3, 0.1, 0.8])
                    sphere(d=tail_length/2, $fn=8);
    }
}

// Render the partridge
rotate([0, 0, 180])
    partridge();

