// Peccary model - realistic style, medium complexity
// Dimensions: ~80mm long, 40mm tall, 30mm wide

// Body parameters
body_length = 60;
body_height = 35;
body_width = 25;

// Head parameters
head_length = 20;
head_width = 15;
head_height = 12;
snout_length = 8;

// Leg parameters
leg_height = 15;
leg_width = 6;
leg_depth = 8;

// Ear parameters
ear_size = 5;

// Tail parameters
tail_length = 10;
tail_width = 4;

module body() {
    // Main torso
    translate([0, 0, leg_height + body_height/2])
    scale([1, 0.8, 0.6])
    sphere(d=body_width);

    // Back hump
    translate([-body_length/3, 0, leg_height + body_height*0.8])
    scale([1.2, 1, 0.8])
    sphere(d=body_width*0.8);

    // Belly
    translate([0, 0, leg_height + 5])
    scale([1.1, 0.7, 0.5])
    sphere(d=body_width*0.7);
}

module head() {
    // Main head
    translate([body_length/2 + head_length/2, 0, leg_height + body_height*0.8])
    rotate([0, -10, 0])
    scale([1.5, 1, 1])
    sphere(d=head_width);

    // Snout
    translate([body_length/2 + head_length + snout_length/2, 0, leg_height + body_height*0.7])
    scale([2, 0.7, 0.5])
    sphere(d=snout_length);

    // Nose
    translate([body_length/2 + head_length + snout_length + 2, 0, leg_height + body_height*0.7])
    sphere(d=3);
}

module ears() {
    // Left ear
    translate([body_length/2 + head_length/2 - 3, head_width/2, leg_height + body_height*0.8 + 8])
    rotate([0, 0, -30])
    scale([0.5, 1, 2])
    sphere(d=ear_size);

    // Right ear
    translate([body_length/2 + head_length/2 - 3, -head_width/2, leg_height + body_height*0.8 + 8])
    rotate([0, 0, 30])
    scale([0.5, 1, 2])
    sphere(d=ear_size);
}

module legs() {
    // Front left leg
    translate([body_length/3, body_width/3, leg_height/2])
    scale([0.8, 0.9, 1])
    cylinder(h=leg_height, d=leg_width);

    // Front right leg
    translate([body_length/3, -body_width/3, leg_height/2])
    scale([0.8, 0.9, 1])
    cylinder(h=leg_height, d=leg_width);

    // Back left leg
    translate([-body_length/3, body_width/3, leg_height/2])
    scale([0.9, 1, 1])
    cylinder(h=leg_height, d=leg_width);

    // Back right leg
    translate([-body_length/3, -body_width/3, leg_height/2])
    scale([0.9, 1, 1])
    cylinder(h=leg_height, d=leg_width);

    // Hooves
    for(x = [body_length/3, -body_length/3])
        for(y = [body_width/3, -body_width/3]) {
            translate([x, y, 1])
            scale([1.1, 1.2, 0.3])
            sphere(d=leg_width);
        }
}

module tail() {
    translate([-body_length/2 - tail_length/2, 0, leg_height + body_height*0.6])
    rotate([0, 20, 0])
    scale([3, 1, 0.5])
    sphere(d=tail_width);
}

module eyes() {
    // Left eye
    translate([body_length/2 + head_length/2 + 2, head_width/3, leg_height + body_height*0.8 + 3])
    sphere(d=2);

    // Right eye
    translate([body_length/2 + head_length/2 + 2, -head_width/3, leg_height + body_height*0.8 + 3])
    sphere(d=2);
}

module tusks() {
    // Lower tusks
    for(y = [1.5, -1.5]) {
        translate([body_length/2 + head_length + snout_length - 1, y, leg_height + body_height*0.6])
        rotate([0, 0, y*60])
        scale([0.3, 0.3, 1.5])
        cylinder(h=4, d=2);
    }
}

// Assemble the peccary
union() {
    body();
    head();
    ears();
    legs();
    tail();

    // Eyes (inset)
    difference() {
        eyes();
        translate([body_length/2 + head_length/2 + 2.5, head_width/3, leg_height + body_height*0.8 + 3.5])
        sphere(d=1);
        translate([body_length/2 + head_length/2 + 2.5, -head_width/3, leg_height + body_height*0.8 + 3.5])
        sphere(d=1);
    }

    tusks();
}

