// OpenSCAD Phoebe (realistic style, medium complexity)

// Overall size: 70mm wingspan, 35mm tail to beak
// Wall thickness: 2mm for 3D printability

// Main body module
module body() {
    // Central body - ellipsoid approximation with spheres
    translate([-5, 0, 5]) scale([1.5, 1.2, 1]) sphere(r=10, $fn=32);
    // Slight bulge for chest
    translate([-8, 0, 3]) scale([1.2, 1.1, 0.8]) sphere(r=8, $fn=32);
}

// Head module
module head() {
    // Main head sphere
    sphere(r=8, $fn=32);

    // Beak - cone made from cylinder
    translate([8, 0, -2]) rotate([0, -20, 0]) cylinder(h=12, r1=3, r2=1, $fn=16);

    // Eyes - small spheres
    translate([3, 4, 2]) sphere(r=1.5, $fn=16);
    translate([3, -4, 2]) sphere(r=1.5, $fn=16);
}

// Wing module with feather details
module wing(side) {
    // Mirror left/right
    mirror([side, 0, 0]) {
        // Main wing shape - tapered ellipsoid
        translate([0, 25, 5]) rotate([0, 0, -20]) 
            scale([0.8, 2.5, 0.3]) sphere(r=10, $fn=32);

        // Primary feathers - staggered cylinders
        for(i=[0:3]) {
            translate([i*3-3, 35-i*2, 3]) rotate([0, 0, -30-i*5])
                cylinder(h=8, r1=2, r2=1, $fn=8);
        }

        // Secondary feathers
        for(i=[0:2]) {
            translate([-2-i*2, 25-i*3, 2]) rotate([0, 0, -15])
                cylinder(h=6, r1=1.5, r2=0.8, $fn=8);
        }
    }
}

// Tail module
module tail() {
    // Central tail feathers
    for(i=[-1:1]) {
        translate([-15, i*4, 2]) rotate([0, 10, 0])
            cylinder(h=12, r1=3, r2=2, $fn=8);
    }

    // Outer tail feathers
    for(i=[-2, 2]) {
        translate([-12, i*6, 1]) rotate([0, 5, i*5])
            cylinder(h=10, r1=2, r2=1.5, $fn=8);
    }
}

// Leg and feet module
module legs() {
    // Left leg
    translate([-5, 4, -8]) cylinder(h=8, r=1.5, $fn=16);
    translate([-5, 4, -16]) {
        // Three forward toes
        for(i=[-1:1]) {
            translate([i*2, 0, 0]) rotate([0, 20, 0])
                cylinder(h=4, r=1, $fn=8);
        }
        // Back toe
        translate([0, -2, 0]) rotate([0, -20, 0])
            cylinder(h=3, r=1, $fn=8);
    }

    // Right leg (mirror of left)
    translate([-5, -4, -8]) cylinder(h=8, r=1.5, $fn=16);
    translate([-5, -4, -16]) {
        for(i=[-1:1]) {
            translate([i*2, 0, 0]) rotate([0, 20, 0])
                cylinder(h=4, r=1, $fn=8);
        }
        translate([0, 2, 0]) rotate([0, -20, 0])
            cylinder(h=3, r=1, $fn=8);
    }
}

// Assemble the phoebe
union() {
    // Main body
    body();

    // Head positioned at front of body
    translate([12, 0, 8]) head();

    // Wings
    wing(1);    // Right wing
    wing(-1);   // Left wing

    // Tail at rear
    tail();

    // Legs and feet
    legs();
}

