// Realistic 3D-printable bird perch
// Overall dimensions ~70mm long, 25mm wide
// No supports needed, all angles ≤45°

module perch_assembly() {
    // Main perch branch with natural wood texture
    union() {
        // Central branch
        perch_branch(0, 0, 0);

        // Left smaller branch
        translate([-20, -8, 5]) rotate([0, 20, -30]) 
            perch_branch(0, 0, 0, 15, 8);

        // Right smaller branch
        translate([20, -8, 5]) rotate([0, 20, 30]) 
            perch_branch(0, 0, 0, 15, 8);

        // Bark texture rings
        for(i = [-25:10:25])
            translate([i, 0, 0])
                bark_ring(10, 11.5);

        // Attachment base
        translate([0, 0, -15])
            base_mount();
    }
}

module perch_branch(x=0, y=0, z=0, len=30, rad=10) {
    translate([x, y, z]) {
        // Main branch cylinder with slight taper
        cylinder(h=len, r1=rad*0.9, r2=rad, $fn=16);

        // Add irregular surface detail
        for(i = [0:30:len]) {
            translate([0, 0, i]) 
                difference() {
                    cylinder(h=3, r=rad*0.8, $fn=16);
                    translate([0, 0, -1]) 
                        cylinder(h=5, r=rad*0.6, $fn=16);
                }
        }
    }
}

module bark_ring(height, radius) {
    // Create rough bark texture
    difference() {
        cylinder(h=height, r=radius, $fn=32);

        // Random bark grooves
        for(angle = [0:45:315]) {
            rotate([0, 0, angle])
                translate([radius*0.7, -radius, 0])
                    cube([radius*0.3, radius*2, height+1]);
        }

        // Knot holes
        translate([radius*0.5, 0, height/2])
            rotate([90, 0, 0])
                cylinder(h=radius*0.6, r=radius*0.2, $fn=8);
    }
}

module base_mount() {
    // Sturdy mounting base for cage attachment
    difference() {
        // Main base
        cylinder(h=8, r=15, $fn=32);

        // Screw hole for mounting
        translate([0, 0, -1])
            cylinder(h=10, r=2.5, $fn=16);

        // Weight reduction cutouts
        for(angle = [0:120:240]) {
            rotate([0, 0, angle])
                translate([7, 0, 2])
                    cylinder(h=6, r=4, $fn=16);
        }
    }

    // Reinforcement ribs
    for(angle = [0:90:270]) {
        rotate([0, 0, angle])
            translate([10, -1.5, 0])
                cube([5, 3, 8]);
    }
}

// Generate the complete perch
perch_assembly();

