// Pipit Bird - Realistic Medium Complexity
// Size: ~80mm wingspan, printable upright

$fn = 32;
body_length = 45;
body_height = 18;
wing_span = 80;
tail_length = 35;
head_size = 12;
beak_length = 10;
leg_length = 15;

module body() {
    // Main body - oval shape
    difference() {
        scale([1.5, 1, 1.2]) 
            sphere(d=body_height);

        // Remove bottom flat
        translate([0, 0, -body_height/2-1])
            cube([body_length*2, body_length*2, body_height], center=true);
    }
}

module head() {
    // Head sphere
    translate([body_length/2+2, 0, body_height/2-2])
        sphere(d=head_size);

    // Beak - cone shape
    translate([body_length/2+head_size/2+beak_length/2, 0, body_height/2-2])
        rotate([0, 90, 0])
            cylinder(h=beak_length, d1=head_size/3, d2=1);
}

module wing() {
    // Main wing shape
    difference() {
        scale([1, 0.2, 0.4])
            sphere(d=wing_span/2);

        // Feather notches
        for(i = [-3:3]) {
            translate([i*8-5, 0, -wing_span/4])
                rotate([0, 10*i, 0])
                    cube([15, 3, 8], center=true);
        }
    }
}

module wings() {
    // Left wing
    translate([-body_length/4, -body_height/2-2, body_height/2-4])
        rotate([0, 0, -20])
            wing();

    // Right wing
    translate([-body_length/4, body_height/2+2, body_height/2-4])
        rotate([0, 0, 20])
            mirror([0,1,0])
                wing();
}

module tail() {
    // Fan tail
    difference() {
        translate([-body_length/2-tail_length/2, 0, -body_height/4])
            scale([1.2, 0.3, 1])
                cube([tail_length, wing_span/3, body_height/2], center=true);

        // Tail feather separation
        for(i = [-2:2]) {
            translate([-body_length/2-5, i*6, -body_height/4])
                cube([tail_length, 1, body_height/2+2], center=true);
        }
    }
}

module legs() {
    // Left leg
    translate([0, -4, -body_height/2-1])
        cylinder(h=leg_length, d=2);

    // Right leg
    translate([0, 4, -body_height/2-1])
        cylinder(h=leg_length, d=2);

    // Feet
    translate([0, -4, -body_height/2-leg_length-1])
        cube([6, 1, 2], center=true);

    translate([0, 4, -body_height/2-leg_length-1])
        cube([6, 1, 2], center=true);
}

module eyes() {
    // Left eye
    translate([body_length/2+head_size/2-2, -head_size/3, body_height/2-1])
        sphere(d=2);

    // Right eye
    translate([body_length/2+head_size/2-2, head_size/3, body_height/2-1])
        sphere(d=2);
}

// Assemble the pipit
union() {
    body();
    head();
    wings();
    tail();
    legs();
    color("black") eyes();
}

