// Approximate height: 70mm

// Global parameters
$fn = 32;
body_color = "silver";
head_color = [0.9, 0.8, 0.7];

// Main assembly
module pilot() {
    // Head
    translate([0, 0, 58]) head();

    // Neck
    translate([0, 0, 54]) neck();

    // Torso
    translate([0, 0, 30]) torso();

    // Arms
    translate([10, 0, 45]) rotate([0, 0, 15]) right_arm();
    translate([-10, 0, 45]) rotate([0, 0, -15]) mirror([1, 0, 0]) right_arm();

    // Legs
