    translate([-3, 0, 0]) mirror([1, 0, 0]) left_leg();

    // Boots
    translate([3, 0, -20]) boot();
    translate([-3, 0, -20]) mirror([1, 0, 0]) boot();
}

// Head with facial features
module head() {
    color(head_color) {
        // Main head shape
        sphere(r = 8);

        // Nose
        translate([0, 7.5, 0]) scale([1, 0.8, 1]) sphere(r = 1.5);

        // Eyes
        translate([2.5, 7, 2]) sphere(r = 1);
        translate([-2.5, 7, 2]) sphere(r = 1);

        // Eyebrows
        translate([2.5, 7.5, 4]) cube([1.5, 0.5, 0.3], center = true);
        translate([-2.5, 7.5, 4]) cube([1.5, 0.5, 0.3], center = true);

        // Mouth
        translate([0, 7, -2]) scale([2, 0.3, 0.5]) sphere(r = 1);

        // Ears
        translate([0, 8, 0]) rotate([90, 0, 0]) cylinder(h = 2, r1 = 2.5, r2 = 2);
        translate([8, 0, 0]) rotate([0, 90, 0]) cylinder(h = 2, r1 = 2.5, r2 = 2);
        translate([-8, 0, 0]) rotate([0, -90, 0]) cylinder(h = 2, r1 = 2.5, r2 = 2);
    }
}

// Neck
module neck() {
    color(body_color) cylinder(h = 4, r = 3.5);
}

// Torso with flight suit details
module torso() {
    color(body_color) {
        // Main torso
        translate([0, 0, 10]) scale([1.2, 0.8, 1]) cylinder(h = 20, r1 = 10, r2 = 8);

        // Flight suit collar
        translate([0, 0, 19]) cylinder(h = 2, r1 = 4, r2 = 5);

        // Chest pockets
        for (i = [-1, 1]) {
            translate([i * 6, 5, 15]) cube([4, 0.5, 3]);
            translate([i * 6, 5.2, 16.5]) cube([3, 0.5, 0.5]);
        }

        // Belt
        translate([0, 4, 10]) cube([18, 0.5, 2]);
    }
}

// Right arm assembly
module right_arm() {
    color(body_color) {
        // Upper arm
        translate([0, 0, -8]) rotate([90, 0, 0]) cylinder(h = 12, r1 = 3.5, r2 = 3);

        // Elbow joint
        translate([0, 0, -15]) sphere(r = 3);

        // Forearm
        translate([0, 4, -15]) rotate([90, 0, 0]) cylinder(h = 10, r1 = 3, r2 = 2.5);

        // Hand
        translate([0, 9, -15]) scale([1, 1.2, 0.8]) sphere(r = 2.5);

        // Fingers
        for (i = [-1, 0, 1]) {
            translate([i * 0.8, 11, -15]) cylinder(h = 2, r = 0.6);
        }
    }
}

// Left leg (right leg is mirrored)
module left_leg() {
    color(body_color) {
        // Thigh
        translate([0, 0, -10]) cylinder(h = 15, r1 = 4, r2 = 3.5);

        // Knee
