// Paddlefish (Polyodon spathula) - Realistic style, medium complexity
// Designed for 3D printing, no overhangs, hollow body

module paddlefish() {
    // Overall scale factor (adjust to fit print bed)
    scale_factor = 0.5;
    scale(scale_factor) {

        // Main body - elongated torpedo shape
        union() {
            // Body segments for smooth shape
            difference() {
                union() {
                    // Main body sections
                    translate([0, 0, 0]) 
                        scale([1, 0.6, 0.4]) 
                        sphere(d=40, $fn=32);

                    translate([20, 0, 0]) 
                        scale([1.2, 0.7, 0.45]) 
                        sphere(d=40, $fn=32);

                    translate([40, 0, 0]) 
                        scale([1.1, 0.6, 0.4]) 
                        sphere(d=30, $fn=32);

                    // Tail extension
                    translate([60, 0, 0]) 
                        scale([0.8, 0.4, 0.3]) 
                        sphere(d=25, $fn=32);
                }

                // Hollow out body for printing
                translate([0, 0, 0]) 
                    scale([0.9, 0.5, 0.35]) 
                    sphere(d=40, $fn=32);

                translate([20, 0, 0]) 
                    scale([1.1, 0.6, 0.4]) 
                    sphere(d=40, $fn=32);

                translate([40, 0, 0]) 
                    scale([1.0, 0.5, 0.35]) 
                    sphere(d=30, $fn=32);

                translate([60, 0, 0]) 
                    scale([0.7, 0.3, 0.25]) 
                    sphere(d=25, $fn=32);
            }

            // Paddle-shaped rostrum
            difference() {
                // Main paddle
                translate([-45, 0, 0]) 
                    rotate([0, 0, 0]) 
                    scale([3, 0.8, 0.15]) 
                    sphere(d=25, $fn=32);

                // Taper the end
                translate([-65, 0, 0]) 
                    rotate([0, 0, 0]) 
                    scale([0.5, 0.6, 0.2]) 
                    cube([20, 30, 10], center=true);

                // Hollow paddle
                translate([-45, 0, -1]) 
                    scale([2.8, 0.7, 0.1]) 
                    sphere(d=25, $fn=32);
            }

            // Dorsal fin
            fin_height = 15;
            fin_length = 30;
            for(i = [0:5]) {
                translate([25 + i*4, 0, 15 + sin(i*30)*3]) 
                    rotate([90, 0, 0]) 
                    cylinder(h=fin_height - i*2, d1=3, d2=1, $fn=8);
            }

            // Caudal fin (upper lobe)
            difference() {
                // Upper caudal fin
                translate([70, 0, 5]) 
                    rotate([0, 0, 20]) 
                    scale([1.5, 0.3, 1]) 
                    cylinder(h=30, d=20, $fn=3);

                // Cut to shape
                translate([70, 0, 15]) 
                    rotate([0, 0, 0]) 
                    cube([40, 20, 20], center=true);
            }

            // Lower caudal fin
            mirror([0, 0, 1]) {
                translate([70, 0, 5]) 
                    rotate([0, 0, 20]) 
                    scale([1.5, 0.3, 1]) 
                    cylinder(h=25, d=18, $fn=3);
            }

            // Pectoral fins (simplified)
