    cube([30, 3, 20], center=true);

    // Fin rays
    for(i = [-14:4:14]) {
        translate([0, 2, 15 + i])
        rotate([0, 15 - abs(i)*0.5, 0])
        scale([0.1, 1, 0.8])
        cube([30, 0.5, 8], center=true);
