    // Head and neck
    translate([22,0,18]) rotate([0,-15,0]) head_neck();

    // Legs
    for(side=[-1,1]) {
        // Front legs
        translate([15, side*6, 0]) leg(16);
        // Rear legs
        translate([-15, side*6, 0]) leg(18);
    }

    // Tail
    translate([-28,0,15]) rotate([0,30,0]) tail();

    // Ears
    for(side=[-1,1]) {
        translate([28, side*5, 31]) rotate([0,10*side,20*side]) ear(side);
    }
}

module torso() {
    // Main barrel
    translate([0,0,15]) scale([1,0.7,0.6]) sphere(20);
    // Chest slope
    translate([18,0,8]) rotate([0,-25,0]) scale([1,0.9,0.5]) cylinder(h=15, r1=12, r2=8);
    // Belly cut
    difference() {
        translate([0,0,14]) cube([40,28,20], center=true);
        translate([0,0,8]) cube([42,30,16], center=true);
    }
}

module head_neck() {
    difference() {
        union() {
            // Neck
            translate([-5,0,0]) rotate([0,-10,0]) scale([0.7,0.7,1]) cylinder(h=20, r=7, $fn=16);
