    translate([head_diameter/2, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=head_diameter*0.6, d=head_diameter/2);

    // Ears
    for (side = [-1, 1]) {
        translate([0, side*(head_diameter/3), head_diameter/3])
        rotate([90, 0, 0])
        cylinder(h=ear_height, d1=head_diameter/4, d2=0);
