            scale([1.2, 0.9, 1.3]) 
                sphere(d = 40);

        // Lower back extension
        translate([0, -15, 20]) 
            scale([1, 0.8, 0.6]) 
                sphere(d = 35);

        // Head
        translate([0, 22, 55]) 
            rotate([0, -20, 0]) 
                potto_head();

        // Limbs
        potto_limbs();

        // Tail
        translate([0, -28, 25]) 
            rotate([45, 0, 0]) 
                potto_tail();
    }
}

// Detailed head module
module potto_head() {
    difference() {
        union() {
            // Main skull
            scale([0.9, 1.1, 1]) 
                sphere(d = 28);

            // Muzzle
            translate([0, 12, -5]) 
                scale([0.7, 0.9, 0.6]) 
                    sphere(d = 15);

            // Ears
            for(side = [-1, 1]) {
                translate([side * 10, 5, 8]) 
                    rotate([0, side * -15, 0]) 
                        scale([0.3, 0.2, 0.6]) 
                            cylinder(h = 10, d1 = 8, d2 = 6);
            }
        }

        // Eye sockets
        for(side = [-1, 1]) {
            translate([side * 7, 8, 3]) 
                sphere(d = 8);
        }

        // Nostrils
        translate([0, 18, -3]) 
            rotate([90, 0, 0]) 
                cylinder(h = 3, d1 = 2, d2 = 0);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([side * 7, 8, 3]) 
            sphere(d = 7);
    }
}

// Limb assembly
module potto_limbs() {
    // Front limbs
    for(side = [-1, 1]) {
        // Upper arm
        translate([side * 15, 10, 35]) 
            rotate([90, 0, side * 20]) 
                potto_limb(20, 6);

        // Lower arm
        translate([side * 25, 0, 15]) 
            rotate([90, 0, side * 30]) 
                potto_limb(15, 5);

        // Hand
        translate([side * 30, -5, 5]) 
            rotate([0, 0, side * 45]) 
                scale([1, 0.5, 0.3]) 
                    sphere(d = 10);
    }

