body_width = 25 * scale_factor;
body_height = 30 * scale_factor;

// Head parameters
head_diameter = 20 * scale_factor;

// Leg parameters
leg_height = 15 * scale_factor;
leg_width = 6 * scale_factor;

// Ear parameters
ear_length = 8 * scale_factor;
ear_width = 5 * scale_factor;

// Tail parameters
tail_length = 15 * scale_factor;
tail_width = 8 * scale_factor;

// Wall thickness for 3D printing
wall_thickness = 1.5 * scale_factor;

// Main body with slight taper
module body() {
    difference() {
        // Main body shape - ellipsoid
        scale([1, 0.8, 1.2]) 
            sphere(d=body_length);

        // Hollow out
        translate([0, 0, -2])
            scale([0.85, 0.65, 1])
                sphere(d=body_length - wall_thickness*2);
    }
}

// Head with snout
module head() {
    union() {
        // Main head sphere
        translate([body_length/2 - 5, 0, body_height/2])
            sphere(d=head_diameter);

        // Snout
        translate([body_length/2 + 8, 0, body_height/2 - 3])
            scale([1.5, 0.7, 0.7])
                sphere(d=head_diameter/2);

        // Nose
        translate([body_length/2 + 15, 0, body_height/2 - 3])
            sphere(d=3);
    }
}

// Ears - small and rounded
module ears() {
    // Left ear
    translate([body_length/2 - 2, -head_diameter/2 - 2, body_height/2 + 8])
        rotate([0, 15, -30])
            scale([1, 0.7, 0.3])
                sphere(d=ear_length);

    // Right ear
    translate([body_length/2 - 2, head_diameter/2 + 2, body_height/2 + 8])
        rotate([0, 15, 30])
            scale([1, 0.7, 0.3])
                sphere(d=ear_length);
}

// Eyes
module eyes() {
    // Left eye
    translate([body_length/2 + 5, -head_diameter/3, body_height/2 + 2])
        color("black")
            sphere(d=2.5);

    // Right eye
    translate([body_length/2 + 5, head_diameter/3, body_height/2 + 2])
        color("black")
            sphere(d=2.5);
}

// Leg module
module leg(x_pos, y_pos, angle) {
    translate([x_pos, y_pos, leg_height/2])
        rotate([0, angle, 0])
            union() {
                // Upper leg
                cylinder(h=leg_height, d=leg_width, $fn=12);

                // Paw - slightly flattened
                translate([0, 0, leg_height/2 + 2])
                    scale([1.2, 1.2, 0.5])
                        sphere(d=leg_width);
