    // Body
    color("black")
    difference() {
        union() {
            // Main body ellipsoid
            scale([1, 0.8, 1.2])
                sphere(r=20);

            // White belly patch
            color("white")
            translate([0, -15, -15])
                scale([0.9, 0.5, 0.6])
                    sphere(r=15);
        }

        // Remove bottom for flat printing
        translate([0, 0, -25])
            cube([100, 100, 50], center=true);
    }

    // Head
    translate([0, 25, 15])
    union() {
        // Main head sphere
        color("black")
        sphere(r=15);

        // White face patch
        color("white")
        translate([0, 5, 0])
            scale([0.8, 0.7, 1])
                sphere(r=12);

        // Beak
        translate([0, 15, -5])
        rotate([0, 20, 0])
        union() {
            // Orange beak
            color("orange")
            scale([1, 0.4, 0.6])
                cylinder(h=20, r1=5, r2=0);

            // Beak stripes
            color("darkorange")
            for(i = [0:2]) {
                translate([i*6-6, 0, -1])
                    scale([0.2, 1, 1.1])
                        cylinder(h=20, r1=5, r2=0);
            }
        }

        // Eyes
        color("black")
        for(x = [-6, 6]) {
            translate([x, 8, 5])
                sphere(r=2);
        }

        // Eye highlights
        color("white")
        for(x = [-6, 6]) {
            translate([x+1, 9, 6])
                sphere(r=0.5);
        }
    }

    // Wings
    module wing() {
        color("black")
        union() {
            // Main wing shape
            translate([0, 0, 5])
                rotate([0, 10, 0])
                scale([1, 0.3, 0.1])
                    cylinder(h=25, r1=15, r2=5);

            // Wing tip
            translate([20, 0, 5])
                rotate([0, 0, -20])
                scale([0.3, 0.2, 0.1])
                    cylinder(h=15, r1=8, r2=3);
        }
