// Pug 3D Model - Realistic Style, Medium Complexity
// Approx 70mm long, 55mm tall, 60mm wide

// Main colors for visual reference (will print in single color)
// Body: beige/light brown
// Face mask: dark brown/black
// Eyes: black
// Collar: optional red

$fn = 32; // Smooth curves

module pug() {
    union() {
        // Main body
        body();

        // Head assembly
        translate([-15, 0, 25]) rotate([0, 10, 0]) head();

        // Legs
        legs();

        // Tail
        translate([35, 0, 25]) rotate([30, 0, 0]) tail();
    }
}

module body() {
    // Main torso - oval shape
    scale([1.2, 0.8, 1]) 
        translate([10, 0, 15]) 
        sphere(d=45);

    // Chest area - slightly protruding
    translate([5, 0, 10])
        scale([0.8, 1.1, 0.6])
        sphere(d=30);

    // Rear hips
    translate([30, 0, 15])
        scale([0.7, 0.9, 0.8])
        sphere(d=25);
}

module head() {
    union() {
        // Main head - squashed sphere
        scale([1.2, 1.1, 0.9])
        sphere(d=35);

        // Snout
        translate([-12, 0, -2])
            scale([1.5, 0.9, 0.7])
            sphere(d=20);

        // Wrinkles on forehead
        for(i = [0:2]) {
            translate([-5, -6 + i*6, 8])
                rotate([0, 90, 0])
                cylinder(h=12, d1=3, d2=1);
        }

        // Eyes
        translate([-8, -8, 6]) eye();
        translate([-8, 8, 6]) mirror([0,1,0]) eye();

        // Nose
        translate([-18, 0, 0])
            scale([1, 0.8, 0.5])
            sphere(d=8);

        // Ears
        translate([0, -10, 12]) ear();
        translate([0, 10, 12]) mirror([0,1,0]) ear();

        // Mouth area wrinkles
        for(i = [-1, 1]) {
            translate([-12, i*5, -5])
                rotate([45*i, 0, 0])
                cylinder(h=8, d1=2, d2=0.5);
        }
    }
}

module eye() {
    difference() {
        // Eye socket
        sphere(d=8);
        // Pupil
        translate([-1, 0, 0])
            sphere(d=4);
    }
}

module ear() {
    rotate([0, -20, -30])
        scale([0.6, 1, 1.5])
        cylinder(h=12, d1=12, d2=6);
}

module legs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([0, side*15, 0])
            leg(25);
    }

    // Rear legs
    for(side = [-1, 1]) {
        translate([30, side*12, 0])
            leg(20);
    }
}

module leg(height) {
    union() {
        // Upper leg
        translate([0, 0, height/2])
            scale([0.8, 0.8, 1])
            sphere(d=height/2.5);

        // Lower leg
        translate([0, 0, height/4])
            cylinder(h=height/1.5, d=height/3);

        // Paw
        translate([0, 0, 0])
            scale([1.2, 1, 0.6])
            sphere(d=height/2.8);
    }
}

module tail() {
    // Curled tail
    rotate([0, 90, 0])
        for(i = [0:30:359]) {
            rotate([0, 0, i])
                translate([10, 0, 0])
                    sphere(d=8);
        }
}

// Generate the pug
pug();

