// Realistic Possum - Medium Complexity
// Designed for 3D printing (no overhangs >45°)

// Overall scale factor
scale_factor = 0.4;

// Body dimensions
body_length = 60 * scale_factor;
body_width = 35 * scale_factor;
body_height = 25 * scale_factor;

// Head dimensions
head_length = 25 * scale_factor;
head_width = 20 * scale_factor;
head_height = 18 * scale_factor;

// Tail dimensions
tail_length = 70 * scale_factor;
tail_diameter = 8 * scale_factor;

// Ear dimensions
ear_radius = 6 * scale_factor;
ear_thickness = 3 * scale_factor;

// Leg dimensions
leg_length = 15 * scale_factor;
leg_diameter = 5 * scale_factor;

// Paw dimensions
paw_radius = 3 * scale_factor;

// Snout dimensions
snout_length = 10 * scale_factor;
snout_width = 8 * scale_factor;
snout_height = 6 * scale_factor;

// Eye dimensions
eye_radius = 2.5 * scale_factor;

// Nose dimensions
nose_radius = 1.5 * scale_factor;

module possum_body() {
    // Main body - egg shape achieved with scaled spheres
    translate([0, 0, body_height/2])
    scale([1, 0.8, 0.6])
    sphere(d=body_length, $fn=32);

    // Belly - slightly flattened bottom
    translate([0, 0, body_height/4])
    scale([1, 0.9, 0.3])
    sphere(d=body_length*0.9, $fn=32);
}

module possum_head() {
    // Main head shape
    translate([body_length/2 + head_length/2, 0, body_height/2 + head_height/4])
    scale([1.2, 1, 1])
    sphere(d=head_length, $fn=32);

    // Snout
    translate([body_length/2 + head_length + snout_length/2, 0, body_height/2 + head_height/4])
    scale([1.5, 1, 0.8])
    sphere(d=snout_length, $fn=32);

    // Eyes
    for(side = [-1, 1]) {
        translate([body_length/2 + head_length*0.7, side*head_width/3, body_height/2 + head_height/3])
        sphere(d=eye_radius*2, $fn=16);

        // Pupils
        translate([body_length/2 + head_length*0.7 + 1, side*head_width/3, body_height/2 + head_height/3 + 0.5])
        sphere(d=eye_radius, $fn=16);
    }

    // Nose
    translate([body_length/2 + head_length + snout_length, 0, body_height/2 + head_height/4])
    sphere(d=nose_radius*2, $fn=16);

    // Ears
    for(side = [-1, 1]) {
        translate([body_length/2 + head_length/3, side*head_width/2, body_height/2 + head_height/2])
        rotate([0, 0, side*30])
        scale([1, 0.7, 0.5])
        sphere(d=ear_radius*2, $fn=16);
    }
}

module possum_tail() {
    // Tail segments for curved shape
    hull() {
        translate([-body_length/2, 0, body_height/2])
        rotate([0, 90, 0])
        cylinder(d=tail_diameter, h=5, $fn=16);

        translate([-body_length/2 - tail_length/4, 0, body_height/2 - tail_length/8])
        rotate([0, 90, 15])
        cylinder(d=tail_diameter*0.9, h=tail_length/4, $fn=16);

        translate([-body_length/2 - tail_length/2, 0, body_height/2 - tail_length/4])
        rotate([0, 90, 30])
        cylinder(d=tail_diameter*0.8, h=tail_length/4, $fn=16);

        translate([-body_length/2 - tail_length*3/4, 0, body_height/2 - tail_length/3])
        rotate([0, 90, 45])
        cylinder(d=tail_diameter*0.7, h=tail_length/4, $fn=16);

        translate([-body_length/2 - tail_length, 0, body_height/2 - tail_length/2.5])
        rotate([0, 90, 60])
        cylinder(d=tail_diameter*0.6, h=tail_length/4, $fn=16);
    }
}

module possum_legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([body_length/4, side*body_width/3, body_height/3])
        rotate([30, 0, 0])
        cylinder(h=leg_length, d=leg_diameter, $fn=16);

        // Lower leg
        translate([body_length/4, side*body_width/3, body_height/3 - leg_length])
        rotate([60, 0, 0])
        cylinder(h=leg_length*0.8, d=leg_diameter*0.8, $fn=16);

        // Paw
        translate([body_length/4, side*body_width/3, body_height/3 - leg_length*1.8])
        sphere(d=paw_radius*2, $fn=16);
    }

    // Rear legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([-body_length/3, side*body_width/3, body_height/3])
        rotate([30, 0, 0])
        cylinder(h=leg_length*1.2, d=leg_diameter*1.1, $fn=16);

        // Lower leg
        translate([-body_length/3, side*body_width/3, body_height/3 - leg_length*1.2])
        rotate([60, 0, 0])
        cylinder(h=leg_length, d=leg_diameter*0.9, $fn=16);

        // Paw
        translate([-body_length/3, side*body_width/3, body_height/3 - leg_length*2.2])
        sphere(d=paw_radius*2.2, $fn=16);
    }
}

// Assemble the possum
union() {
    possum_body();
    possum_head();
    possum_tail();
    possum_legs();
}

