// Realistic Piranha - Medium Complexity
// Overall length: ~90mm, suitable for 3D printing

$fn = 32;
wall = 1.2;

module fish_body() {
    // Main body - elongated ellipsoid
    scale([1, 0.6, 0.4])
        sphere(25);
}

module tail_fin() {
    // Tail fin - two curved plates
    translate([-40, 0, 0]) {
        // Upper lobe
        rotate([0, -20, 0])
            scale([0.3, 2, 0.1])
                sphere(15);
        // Lower lobe
        rotate([0, 20, 0])
            scale([0.3, 2, 0.1])
                sphere(15);
    }
}

module dorsal_fin() {
    // Dorsal fin - triangular shape
    translate([0, 0, 10]) {
        rotate([90, 0, 0])
            linear_extrude(height=15, center=true, convexity=10)
                polygon(points=[[0,0], [20,0], [10,10]]);
    }
}

module pectoral_fins() {
    // Side fins - two symmetric
    for (side = [1, -1]) {
        translate([10, side*12, -5]) {
            rotate([0, side*20, 0])
                scale([1.5, 1, 0.2])
                    sphere(8);
        }
    }
}

module anal_fin() {
    // Anal fin - smaller fin underneath
    translate([-10, 0, -8]) {
        rotate([-90, 0, 0])
            linear_extrude(height=10, center=true, convexity=10)
                polygon(points=[[0,0], [15,0], [7.5,5]]);
    }
}

module head() {
    // Piranha head - more angular
    translate([25, 0, 0]) {
        // Main head shape
        scale([1.2, 0.8, 0.9])
            sphere(15);

        // Snout extension
        translate([10, 0, -2])
            scale([2, 0.8, 0.6])
                sphere(8);
    }
}

module jaws() {
    // Lower jaw
    translate([30, 0, -5]) {
        rotate([-20, 0, 0])
            scale([1.6, 0.8, 0.4])
                sphere(10);
    }
}

module teeth(count=8) {
    // Triangular teeth along jaw
    color("white")
    for (i = [0:count-1]) {
        angle = 360/count * i;
        radius = 15;
        x = 30 + cos(angle) * radius * 0.2;
        y = sin(angle) * radius * 0.4;
        z = -3 - abs(sin(angle)) * 2;

        translate([x, y, z]) {
            rotate([0, 0, angle])
                rotate([20, 0, 0])
                    scale([0.3, 0.3, 1])
                        cylinder(h=3, r1=0.5, r2=0);
        }
    }
}

module eye() {
    // Eyes on sides of head
    for (side = [1, -1]) {
        translate([30, side*8, 3]) {
            // Eye socket
            color("black")
            sphere(3);
            // Eye highlight
            translate([1, 1, 1])
                scale([0.5, 0.5, 0.5])
                    color("white")
                    sphere(1);
        }
    }
}

module gill_slits() {
    // Gill openings
    for (i = [0:2]) {
        translate([15 - i*2, 12, -3 + i*0.5]) {
            rotate([0, 90, 0])
                scale([0.1, 2, 1])
                    cylinder(h=1, r=2);
        }
    }
}

// Assemble the piranha
union() {
    fish_body();
    tail_fin();
    dorsal_fin();
    pectoral_fins();
    anal_fin();
    head();
    jaws();
    teeth();
    eye();
    gill_slits();
}

