// Puma - Medium Complexity Realistic Style
// Designed for 3D printing with no overhangs

$fn = 32;  // Smooth curves for printing

// Main body module
module puma_body() {
    // Torso - elongated ellipsoid
    translate([0, 0, 15])
    scale([1.5, 0.8, 0.6])
    sphere(20);

    // Chest - slightly forward
    translate([-10, 0, 12])
    scale([1.2, 0.9, 0.5])
    sphere(15);

    // Belly - flat bottom for printing
    difference() {
        translate([0, 0, 8])
        scale([1.4, 0.7, 0.4])
        sphere(18);

        translate([0, 0, 0])
        cube([60, 40, 10], center=true);
    }
}

// Head module with facial features
module puma_head() {
    // Main head shape
    translate([28, 0, 22])
    rotate([0, -10, 0])
    scale([1.2, 0.9, 0.8])
    sphere(12);

    // Snout
    translate([35, 0, 20])
    rotate([0, -15, 0])
    scale([0.8, 0.6, 0.5])
    cylinder(h=10, r1=8, r2=5);

    // Ears - triangular
    for(side = [-1, 1]) {
        translate([30, side*10, 30])
        rotate([0, side*20, 0])
        cylinder(h=8, r1=3, r2=0);
    }

    // Eyes - small spheres
    for(side = [-1, 1]) {
        translate([33, side*5, 24])
        color("black")
        sphere(2);
    }

    // Nose
    translate([40, 0, 20])
    color("black")
    sphere(3);
}

// Leg module - reusable for all legs
module leg(position, side, angle) {
    translate(position)
    rotate([0, 0, angle]) {
        // Upper leg
        translate([0, side*5, 0])
        rotate([-90, 0, 0])
        cylinder(h=15, r1=4, r2=3);

        // Lower leg
        translate([0, side*5, -12])
        rotate([-90, 0, 0])
        cylinder(h=12, r1=3, r2=2);

        // Paw - flat bottom
        translate([0, side*5, -20])
        scale([1, 1, 0.5])
        sphere(4);
    }
}

// Tail module
module puma_tail() {
    hull() {
        translate([-30, 0, 18])
        sphere(3);

        translate([-45, 0, 25])
        sphere(2);

        translate([-55, 0, 22])
        sphere(1);
    }
}

// Assembly
union() {
    puma_body();
    puma_head();

    // Front legs
    leg([-5, 12, 0], 1, 5);
    leg([-5, -12, 0], -1, -5);

    // Back legs
    leg([-20, 10, 0], 1, -5);
    leg([-20, -10, 0], -1, 5);

    puma_tail();
}

