// Phalanger 3D Model - Realistic Style with Medium Complexity
// Basic proportions based on sugar glider (Petaurus breviceps)

$fn = 32;  // Smooth curves for realistic appearance

// Overall dimensions (mm)
body_length = 60;
body_height = 25;
tail_length = 45;

module body() {
    // Main torso - oval shape
    union() {
        // Upper torso
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 0.6])
                sphere(d=body_length);

        // Lower belly
        translate([0, 0, body_height/4])
            scale([1.1, 0.7, 0.4])
                sphere(d=body_length*0.9);
    }
}

module head() {
    // Position head at front of body
    translate([body_length/2 + 8, 0, body_height/2 + 5]) {
        union() {
            // Main head sphere
            sphere(d=20);

            // Snout extension
            translate([8, 0, -2])
                scale([1.5, 0.8, 0.8])
                    sphere(d=12);

            // Ears - positioned on sides
            for(side = [-1, 1]) {
                translate([0, side*8, 5])
                    rotate([0, side*20, 0])
                        scale([0.3, 1, 1.5])
                            sphere(d=10);
            }
        }

        // Eyes - simple spheres
        for(side = [-1, 1]) {
            translate([3, side*6, 2])
                color("black")
                    sphere(d=3);
        }
    }
}

module limbs() {
    // Front limbs (arms)
    for(side = [-1, 1]) {
        // Shoulder position
        translate([body_length/4, side*body_length/3, body_height/2]) {
            rotate([0, 0, side*30])
                // Upper arm
                cylinder(h=15, d1=6, d2=4);

            // Lower arm and hand
            translate([12, 0, 0])
                rotate([0, 90, side*20])
                    cylinder(h=12, d1=4, d2=3);

            // Hand
            translate([12, 6, 0])
                sphere(d=6);
        }
    }

    // Hind limbs (legs)
    for(side = [-1, 1]) {
        // Hip position
        translate([-body_length/4, side*body_length/4, body_height/3]) {
            rotate([20, 0, side*45])
                // Upper leg
                cylinder(h=18, d1=7, d2=5);

            // Lower leg
            translate([0, 10, -12])
                rotate([30, 0, 0])
                    cylinder(h=15, d1=5, d2=4);

            // Foot
            translate([0, 15, -18])
                rotate([0, side*20, 0])
                    scale([2, 1, 0.5])
                        sphere(d=8);
        }
    }
}

module tail() {
    // Position tail at rear of body
    translate([-body_length/2 - 5, 0, body_height/3]) {
        // Tapered tail segments for natural curve
        for(i = [0:4]) {
            rotate([0, 5*i, 0])
                translate([-i*tail_length/5, 0, -i*2])
                    cylinder(h=tail_length/5, 
                           d1=8-i, 
                           d2=7-i);
        }
    }
}

module patagium() {
    // Gliding membrane between front and hind limbs
    // Create wedge shape between limbs

    // Right side membrane
    translate([0, body_length/3, body_height/3]) {
        // Points for membrane polygon
        polyhedron(
            points = [
                [body_length/4, 0, 0],           // front limb
                [-body_length/4, 0, 0],          // rear limb
                [-body_length/4, 0, -10],        // bottom rear
                [body_length/4, 0, -8],          // bottom front
                [body_length/4, -15, -5],        // wing tip
                [-body_length/4, -15, -8]        // wing tip rear
            ],
            faces = [
                [0,1,2,3],  // top surface
                [0,4,5,1],  // side surface
                [0,3,4],    // front triangle
                [1,5,2],    // rear triangle
                [3,2,5,4]   // bottom surface
            ]
        );
    }

    // Mirror for left side
    mirror([0,1,0])
        translate([0, body_length/3, body_height/3]) {
            polyhedron(
                points = [
                    [body_length/4, 0, 0],
                    [-body_length/4, 0, 0],
                    [-body_length/4, 0, -10],
                    [body_length/4, 0, -8],
                    [body_length/4, -15, -5],
                    [-body_length/4, -15, -8]
                ],
                faces = [
                    [0,1,2,3],
                    [0,4,5,1],
                    [0,3,4],
                    [1,5,2],
                    [3,2,5,4]
                ]
            );
        }
}

// Main assembly
union() {
    body();
    head();
    limbs();
    tail();
    patagium();
}

// Add support base for 3D printing
translate([0, 0, -5])
    cube([body_length + 20, body_length/2, 2], center=true);

