// Quokka model for 3D printing
// Medium complexity realistic style
// Approx 60mm long x 40mm wide x 35mm high

$fn = 32;
body_length = 50;
body_width = 35;
body_height = 25;

module quokka() {
    // Main body - slightly elongated oval
    translate([0, 0, body_height/2])
    scale([1.2, 1, 1])
    sphere(d=body_height);

    // Head - smaller sphere attached to body
    translate([-body_length/3, 0, body_height + 8])
    sphere(d=20);

    // Ears - two rounded ears on top of head
    rotate([0, 30, 0])
    translate([-body_length/3 - 5, 8, body_height + 15])
    scale([0.8, 0.4, 1])
    sphere(d=12);

    mirror([0, 1, 0])
    rotate([0, 30, 0])
    translate([-body_length/3 - 5, 8, body_height + 15])
    scale([0.8, 0.4, 1])
    sphere(d=12);

    // Snout - small protruding nose
    translate([-body_length/3 - 10, 0, body_height + 6])
    sphere(d=8);

    // Eyes - two small spheres
    translate([-body_length/3 - 8, 5, body_height + 12])
    sphere(d=3);

    translate([-body_length/3 - 8, -5, body_height + 12])
    sphere(d=3);

    // Arms - cylindrical front legs
    rotate([20, 0, 30])
    translate([-5, 15, body_height/2 - 5])
    cylinder(h=15, d1=8, d2=5);

    mirror([0, 1, 0])
    rotate([20, 0, 30])
    translate([-5, 15, body_height/2 - 5])
    cylinder(h=15, d1=8, d2=5);

    // Legs - rear legs with feet
    for(side = [-1, 1]) {
        // Upper leg
        rotate([0, 0, side * 20])
        translate([body_length/3, side * 8, 0])
        cylinder(h=12, d=10);

        // Lower leg
        rotate([0, 10, side * 20])
        translate([body_length/3 + 8, side * 12, -8])
        cylinder(h=15, d=8);

        // Foot
        rotate([0, 0, side * 20])
        translate([body_length/3 + 12, side * 15, -18])
        scale([1.5, 0.8, 0.5])
        sphere(d=10);
    }

    // Tail - short rounded tail
    rotate([0, 30, 0])
    translate([body_length/2 + 5, 0, body_height/3])
    scale([1, 1, 0.8])
    sphere(d=12);

    // Fur texture - small bumps across body
    for(i = [0:3]) {
        for(j = [0:5]) {
            angle = j * 60;
            radius = 8 + i * 5;
            height = body_height/2 + 5 + i * 2;
            translate([0, cos(angle) * radius, height])
            sphere(d=2);
        }
    }
}

// Generate the complete quokka
quokka();

