// Raccoon - Medium Complexity Realistic Style
// Overall size ~60mm long, 30mm wide, 35mm tall

// Main parameters
body_length = 40;
body_height = 25;
body_width = 22;
head_diameter = 18;
leg_height = 8;
leg_width = 4;
tail_length = 25;
tail_width = 8;
ear_size = 4;

// Wall thickness for 3D printing
wall = 1.2;

module raccoon() {
    // Body
    union() {
        // Torso - main body
        translate([0, 0, leg_height])
            scale([1, 0.8, 0.7])
            sphere(d=body_width, $fn=32);

        // Chest - forward extension
        translate([body_length/3, 0, leg_height])
            scale([0.8, 0.9, 0.6])
            sphere(d=body_width*0.9, $fn=32);

        // Head
        translate([body_length/2 + 5, 0, leg_height + body_height/2])
            sphere(d=head_diameter, $fn=32);

        // Snout
        translate([body_length/2 + 12, 0, leg_height + body_height/2 - 2])
            scale([1.5, 0.8, 0.8])
            sphere(d=head_diameter*0.6, $fn=32);

        // Ears
        translate([body_length/2 + 5, head_diameter/2 - 2, leg_height + body_height/2 + head_diameter/2 - 3])
