// Rat - Realistic Style, Medium Complexity
// Main Dimensions: ~80mm nose-to-tail, ~35mm tall

$fn = 24;  // Smooth curves for printing

// Overall scale factor
scale_factor = 1;

// Main body
module rat_body() {
    // Torso - slightly elongated ellipsoid
    translate([0, 0, 12]) 
        scale([1.2, 1, 0.8])
        sphere(15);

    // Rump
    translate([-20, 0, 10])
        scale([1.3, 1.1, 1])
        sphere(12);

    // Chest area
    translate([15, 0, 10])
        scale([0.9, 0.8, 0.7])
        sphere(12);
}

// Head with snout
module rat_head() {
    // Main head sphere
    translate([25, 0, 18])
        sphere(10);

    // Snout extension
    translate([35, 0, 16])
        scale([1.5, 0.8, 0.8])
        sphere(6);

    // Nose tip
    translate([42, 0, 16])
        sphere(3);
}

// Ears
module rat_ears() {
    // Left ear
    translate([22, -10, 27]) {
        rotate([0, -20, -30])
            scale([1, 0.3, 1.5])
            sphere(5);
    }

    // Right ear
    translate([22, 10, 27]) {
        rotate([0, -20, 30])
            scale([1, 0.3, 1.5])
            sphere(5);
    }
}

// Eyes
module rat_eyes() {
    // Left eye
    translate([32, -8, 20])
        sphere(2.5);

    // Right eye
    translate([32, 8, 20])
        sphere(2.5);
}

// Whiskers
module rat_whiskers() {
    // Left whiskers
    for(angle = [-30, 0, 30]) {
        translate([40, -7, 16]) {
            rotate([0, 0, angle])
                rotate([90, 0, 0])
                    cylinder(h=12, r1=0.5, r2=0.3);
        }
    }

    // Right whiskers
    for(angle = [-30, 0, 30]) {
        translate([40, 7, 16]) {
            rotate([0, 0, -angle])
                rotate([90, 0, 0])
                    cylinder(h=12, r1=0.5, r2=0.3);
        }
    }
}

// Legs
module rat_legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([15, side*10, 8]) {
            rotate([0, side*30, 0])
                cylinder(h=10, r1=3, r2=2.5);
        }

        // Foot
        translate([12, side*13, -2]) {
            rotate([0, 0, side*20])
                scale([1.5, 1, 0.5])
                sphere(3);
        }
    }

    // Rear legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([-15, side*8, 6]) {
            rotate([0, side*15, 0])
                cylinder(h=12, r1=4, r2=3);
        }

        // Lower leg
        translate([-18, side*10, -4]) {
            rotate([0, side*15, 0])
                cylinder(h=8, r1=2.5, r2=2);
        }

        // Foot
        translate([-20, side*12, -8]) {
            scale([2, 1.2, 0.6])
            sphere(3.5);
        }
    }
}

// Tail
module rat_tail() {
    // Segmented tail for printability
    for(i = [0:10]) {
        translate([-25 - i*3, 0, 8 - i*0.5]) {
            rotate([0, 0, -i*5])
                sphere(3 - i*0.15);
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    rat_body();
    rat_head();
    rat_ears();
    rat_eyes();
    rat_whiskers();
    rat_legs();
    rat_tail();
}

