module body() {
    difference() {
        // Torso
        translate([0, 0, 15]) scale([1.2, 1, 1]) sphere(20);

        // Neck cavity
        translate([30, 0, 25]) rotate([0, 90, 0]) cylinder(h=25, r1=10, r2=12);
    }
}

// Head
module head() {
    translate([45, 0, 35]) rotate([0, -20, 0]) {
        union() {
            // Skull
            scale([1.3, 0.9, 1]) sphere(12);

            // Snout
            translate([12, 0, -3]) scale([1.5, 0.8, 0.8]) sphere(8);

            // Ears
            for(side=[-1, 1]) {
                translate([0, side*10, 5]) rotate([0, -20*side, 20]) 
                    scale([0.3, 0.1, 0.5]) cylinder(h=15, r1=5, r2=2);
            }

            // Eyes
            for(side=[-1, 1]) {
                translate([8, side*6, 3]) sphere(2.5);
            }
        }
    }
}

// Horns
module horns() {
    for(side=[-1, 1]) {
        translate([40, side*10, 45]) rotate([0, 0, 10*side]) {
            // Base curl
            rotate_extrude(angle=270, $fn=16) 
                translate([8, 0, 0]) circle(r=3);

            // Second curl
            translate([-3, 3, 8]) rotate([30, 0, 0]) rotate_extrude(angle=180, $fn=12) 
                translate([5, 0, 0]) circle(r=2);
        }
    }
}

// Legs
module legs() {
    for(x=[-15, 15], side=[-1, 1]) {
        translate([x, side*15, 0]) {
            // Upper leg
            translate([0, 0, 15]) rotate([10*side, 0, 0]) 
                cylinder(h=20, r=5, r1=5, r2=4);

            // Lower leg
            translate([0, 0, 6]) rotate([10*side, 0, 0]) 
                cylinder(h=15, r=4, r1=4, r2=3);

            // Hoof
            translate([0, 0, 0]) scale([1.2, 1, 0.5]) sphere(4);
        }
    }
}

// Tail
module tail() {
    translate([-25, 0, 15]) rotate([0, 45, 0]) {
        // Tail base
        cylinder(h=15, r=4, r1=4, r2=2);

        // Tuft
        translate([0, 0, -8]) sphere(5);
    }
}

