// Reindeer - Medium complexity realistic style
// Overall size approximately 80mm long

// Parameters for easy resizing
body_length = 40;
body_height = 25;
body_width = 20;
leg_length = 30;
head_length = 20;
head_height = 15;
antler_height = 35;

// Main body - oval shape
module body() {
    difference() {
        // Main torso
        scale([1.2, 0.8, 1])
        sphere(d=body_height, $fn=30);

        // Neck indentation
        translate([body_length/2-5, 0, body_height/3])
        rotate([0, 90, 0])
        cylinder(h=15, r1=8, r2=5, $fn=20);
    }
}

// Head with snout
module head() {
    union() {
        // Skull
        translate([body_length/2+head_length/2, 0, body_height+5])
        scale([1.3, 0.8, 1])
        sphere(d=head_height, $fn=25);

        // Snout
        translate([body_length/2+head_length, 0, body_height+2])
        rotate([0, -20, 0])
        cylinder(h=head_length/2, r1=head_height/3, r2=head_height/4, $fn=20);

        // Nose
        translate([body_length/2+head_length+2, 0, body_height+1])
        sphere(d=4, $fn=15);
    }
}

// Ear module
module ear(side) {
    translate([body_length/2+head_length/2-5, side*6, body_height+12])
    rotate([0, 0, side*20])
    scale([0.5, 0.3, 1])
    cylinder(h=8, r1=4, r2=2, $fn=8);
}

// Eye module
module eye(side) {
    translate([body_length/2+head_length/2, side*5, body_height+8])
    sphere(d=3, $fn=10);
}

// Leg module
module leg(offset_x, offset_y) {
    translate([offset_x, offset_y, -leg_length/2])
    rotate([0, 10, 0])
    cylinder(h=leg_length, r1=3, r2=2, $fn=12);
}

// Hoof module
module hoof(offset_x, offset_y) {
    translate([offset_x, offset_y, -leg_length-2])
    scale([1.2, 0.8, 0.5])
    sphere(d=5, $fn=10);
}

// Antler branch module
module antler_branch(level, angle, length) {
    if (level > 0) {
        rotate([0, 0, angle])
        translate([length, 0, 0])
        cylinder(h=length, r1=1.5, r2=1, $fn=8);

        antler_branch(level-1, 30, length*0.7);
        antler_branch(level-1, -30, length*0.7);
    }
}

// Main antler assembly
module antler(side) {
    translate([body_length/2+head_length/2-2, side*5, body_height+15])
    rotate([0, 20, 0])
    cylinder(h=antler_height/2, r1=2, r2=1.5, $fn=8);

    translate([body_length/2+head_length/2-2, side*5, body_height+15+antler_height/2])
    rotate([0, -10, 0])
    antler_branch(2, 0, antler_height/3);
    antler_branch(2, 45, antler_height/4);
    antler_branch(2, -45, antler_height/4);
}

// Tail
module tail() {
