// Redstart 3D Model (Medium Realistic)
// Designed for 3D printing (no overhangs, 2mm wall thickness)

$fn = 50;

// Main bird assembly
module redstart() {
    // Body
    body();

    // Head
    translate([0, 18, 8]) head();

    // Tail
    translate([-20, 0, 0]) tail();

    // Wings
    translate([0, 0, 10]) wings();

    // Legs
    translate([5, -5, -8]) leg();
    translate([-5, -5, -8]) leg();
}

// Bird body (oval shape)
module body() {
    union() {
        // Main body
        scale([1.2, 1, 1.1]) rotate([90, 0, 0]) 
            cylinder(h = 20, r1 = 6, r2 = 5);

        // Chest bulge
        translate([0, 5, 0])
            scale([0.8, 0.6, 0.8]) sphere(7);
    }
}

// Head with beak and eyes
module head() {
    union() {
        // Head sphere
        sphere(5);

        // Beak (cone shape)
        translate([0, 5.5, -1]) rotate([0, -20, 0])
            cylinder(h = 4, r1 = 2, r2 = 0.5);

        // Eye left
        translate([2, 3, 2]) sphere(1);

        // Eye right
        translate([-2, 3, 2]) sphere(1);

        // Eye pupils
        translate([2.2, 3.5, 2.2]) color("black") sphere(0.5);
        translate([-1.8, 3.5, 2.2]) color("black") sphere(0.5);
    }
}

// Tail feathers (fan shape)
module tail() {
    union() {
        // Central tail feather
        translate([0, 0, 0]) rotate([0, -10, 0])
            scale([1, 0.2, 0.1]) cube([15, 8, 1]);

        // Side feathers (using loop)
        for (i = [-1:1]) {
            translate([i*3, 0, i*2]) rotate([0, -10-i*5, i*15])
                scale([0.8, 0.15, 0.08]) cube([12, 7, 0.8]);
        }
    }
}

// Wings
module wings() {
    union() {
        // Left wing
        translate([7, 0, 2]) rotate([0, 0, 30])
            wing_shape();

        // Right wing
        translate([-7, 0, 2]) mirror([1, 0, 0]) rotate([0, 0, 30])
            wing_shape();
    }
}

// Wing shape (curved)
module wing_shape() {
    difference() {
        // Main wing
        translate([0, 0, 0]) rotate([0, -10, 0])
            scale([1, 0.3, 0.15]) cylinder(h = 12, r1 = 8, r2 = 4);

        // Wing tip curve
        translate([8, 0, 0]) rotate([0, 90, 0])
            cylinder(h = 3, r = 2.5);

        // Feather details (small indentations)
        for (i = [1:3]) {
            translate([2*i, 0, -0.5]) rotate([0, -10, 0])
                scale([0.3, 0.2, 0.1]) cube([2, 3, 0.5]);
        }
    }
}

// Leg with foot
module leg() {
    union() {
        // Upper leg
        translate([0, 0, 0]) rotate([0, 0, 10])
            cylinder(h = 4, r = 0.8);

        // Lower leg
        translate([0, -1, -4]) rotate([0, 0, -10])
            cylinder(h = 3, r = 0.6);

        // Foot
        translate([0, -2, -7]) foot();
    }
}

// Bird foot with toes
module foot() {
    union() {
        // Main foot pad
        translate([0, 0, 0]) scale([1.5, 0.5, 0.3]) sphere(1.5);

        // Toes (front)
        for (i = [-1:1]) {
            translate([i*0.8, -1.2, 0]) rotate([0, 0, i*10])
                cylinder(h = 2, r = 0.3);
        }

        // Back toe
        translate([0.5, 0.5, 0]) rotate([0, 0, 120])
            cylinder(h = 1.5, r = 0.3);
    }
}

// Generate the bird
redstart();

