// Rhinoceros - Medium complexity realistic style
// Designed for 3D printing, no supports needed

$fn = 36;

// Body dimensions
body_length = 60;
body_width = 25;
body_height = 30;

// Head dimensions
head_length = 25;
head_width = 15;
head_height = 20;

// Leg dimensions
leg_height = 25;
leg_width = 8;
leg_depth = 10;

// Horn dimensions
horn_base = 5;
horn_height = 12;

module rhino() {
    // Main body - ellipsoid shape
    union() {
        // Central body
        scale([1, 0.8, 0.6])
            sphere(d=body_width);

        // Extended rear
        translate([-20, 0, -5])
            scale([1.5, 1, 0.8])
                sphere(d=body_width * 0.9);

        // Extended front
        translate([20, 0, -3])
            scale([1.2, 0.9, 0.7])
                sphere(d=body_width * 0.8);
    }

    // Head
    translate([body_length/2 + 5, 0, body_height/3])
    rotate([0, -20, 0])
    union() {
        // Main head shape
        scale([1.3, 0.8, 0.7])
            sphere(d=head_width);

        // Snout extension
        translate([head_length/2, 0, -head_height/3])
            scale([1.5, 0.7, 0.5])
                sphere(d=head_width * 0.8);
    }

    // Horn
    translate([body_length/2 + 20, 0, body_height/3 + 5])
    rotate([0, -20, 0])
    cylinder(h1=horn_height, r1=horn_base, r2=0.5, h=horn_height);

    // Second smaller horn
    translate([body_length/2 + 18, 0, body_height/3 + 8])
    rotate([0, -20, 0])
    cylinder(h1=horn_height*0.7, r1=horn_base*0.6, r2=0.3, h=horn_height*0.7);

    // Legs using loop for all four
    module leg(side, front) {
        x_pos = front ? body_length/2 - 10 : -body_length/2 + 10;
        y_pos = side * body_width/2 - 5;
        translate([x_pos, y_pos, -leg_height/2 - 5])
            scale([1, 1, 0.8])
                cylinder(h=leg_height, r1=leg_width/2, r2=leg_width/2);
    }

    // Front legs
    leg(1, true);
    leg(-1, true);

    // Rear legs
    leg(1, false);
    leg(-1, false);

    // Ears
    module ear(side) {
        translate([body_length/2 - 5, side * body_width/2, body_height/2 + 5])
        rotate([0, 0, side * 30])
        scale([0.3, 1, 0.5])
            sphere(d=8);
    }

    ear(1);
    ear(-1);

    // Tail
    translate([-body_length/2, 0, -5])
    rotate([0, 45, 0])
    cylinder(h=15, r1=3, r2=1);

    // Eyes
    module eye(side) {
        translate([body_length/2 + 15, side * head_width/3, body_height/3 + 3])
        sphere(d=3);
    }

    eye(1);
    eye(-1);

    // Nostrils
    module nostril(side) {
        translate([body_length/2 + 22, side * head_width/4, body_height/3 - 5])
        rotate([0, -20, 0])
        cylinder(h=3, r=2);
    }

    nostril(1);
    nostril(-1);
}

