// Realistic medium-complexity rail for model scenery
// Overall length 80 mm, height 12 mm, width 15 mm
// 3D-printable, no steep overhangs, 0.8 mm minimum walls

// Parameters
rail_len   = 80;      // total length of the rail segment
tie_pitch  = 8;       // center-to-center distance of ties
tie_cnt    = floor(rail_len/tie_pitch)+1;
tie_w      = 15;      // tie width
tie_t      = 2.5;     // tie thickness
rail_h     = 12;      // overall rail height above ties
head_w     = 3.0;     // rail head width
head_h     = 2.2;     // rail head thickness
web_w      = 1.2;     // rail web thickness
foot_w     = 6.0;     // rail foot width
foot_h     = 2.0;     // rail foot thickness
clearance  = 0.4;     // extra space for print tolerance

module rail_profile() {
    // Centered on Z=0, extends upward
    // Head
    translate([0, 0, rail_h - head_h])
        cube([head_w, rail_len, head_h], center=true);

    // Web
    translate([0, 0, foot_h])
        cube([web_w, rail_len, rail_h - head_h - foot_h], center=true);

    // Foot (base)
    difference() {
        cube([foot_w, rail_len, foot_h], center=true);
        // Bolt holes along foot
        for(i=[-rail_len/2+5 : 10 : rail_len/2-5])
            translate([0, i, foot_h/2])
                rotate([90, 0, 0])
                    cylinder(h=foot_w+1, d=2, $fn=12, center=true);
    }
}

module tie() {
    // Single wooden tie with wood-grain texture simulated by small cuts
    difference() {
        cube([tie_w, tie_t, 2], center=true);
        // Cut shallow grooves for texture
        for(x=[-tie_w/2+2 : 2 : tie_w/2-2])
            translate([x, 0, 0])
                cube([0.5, tie_t+0.1, 1.0], center=true);
    }
}

// Assembly
// Ties
for(i=[0:tie_cnt-1])
    translate([-rail_len/2 + i*tie_pitch, 0, 0])
        tie();

// Left rail
translate([-rail_gauge/2, 0, 2])
    rail_profile();

// Right rail
rail_gauge = 16.5; // HO/OO gauge in mm
translate([rail_gauge/2, 0, 2])
    rail_profile();

// Optional spike heads (small hemispheres)
module spike() {
    translate([0, 0, 2])
        scale([1, 0.4, 0.4])
            sphere(d=1.5, $fn=8);
}

for(i=[0:tie_cnt-1])
    for(side=[-1, 1])
        translate([-rail_len/2 + i*tie_pitch, side*tie_t/3, 0])
            spike();

