// Realistic 3D Printable Pitta (Bird)
// Medium complexity model using basic OpenSCAD primitives
// Overall size: ~80mm length

$fn = 32;  // Smooth curves for 3D printing

module pitta() {
    // Body proportions
    body_length = 40;
    body_height = 25;
    body_width = 22;

    // Head
    translate([body_length/2 - 5, 0, body_height/2 + 8]) {
        // Main head sphere
        sphere(d=18);

        // Beak
        translate([9, 0, -2]) {
            rotate([0, -20, 0]) {
                cylinder(h=12, d1=3, d2=1.5);
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([3, side*5, 2]) {
                sphere(d=4);
                translate([1, 0, 0]) {
                    sphere(d=2);
                }
            }
        }
    }

    // Main body
    translate([0, 0, body_height/2]) {
        // Torso - ellipsoid shape
        scale([1, 0.8, 0.7]) {
            sphere(d=body_length);
        }
    }

    // Wings
    for(side = [-1, 1]) {
        translate([-5, side*12, body_height/2 + 5]) {
            rotate([0, side*10, -20]) {
                // Wing shape
                difference() {
                    scale([1.2, 0.3, 0.8]) {
                        sphere(d=body_length*0.7);
                    }
                    // Wing feather details
                    for(i = [1:3]) {
                        translate([-5 + i*3, 0, -2 + i]) {
                            rotate([0, 0, -10*i]) {
                                cube([8, 1, 1], center=true);
                            }
                        }
                    }
                }
            }
        }
    }

    // Tail
    translate([-body_length/2 - 8, 0, body_height/2 - 3]) {
        // Fan tail
        for(angle = [-30:15:30]) {
            rotate([0, 0, angle]) {
                translate([-5, 0, 0]) {
                    scale([1, 0.1, 0.4]) {
                        cylinder(h=15, d=8);
                    }
                }
            }
        }
    }

    // Legs
    for(side = [-1, 1]) {
        translate([side*5, 0, 0]) {
            // Upper leg
            cylinder(h=12, d=3);

            // Lower leg
            translate([0, 0, 12]) {
                rotate([20, 0, 0]) {
                    cylinder(h=10, d=2.5);

                    // Foot
                    translate([0, 0, 10]) {
                        // Toes forward
                        for(toe = [-1, 0, 1]) {
                            translate([toe*3, 2, 0]) {
                                rotate([0, 90, 0]) {
                                    cylinder(h=6, d=1.5);
                                }
                            }
                        }
                        // Back toe
                        translate([0, -3, 0]) {
                            rotate([0, 90, 0]) {
                                cylinder(h=4, d=1.5);
                            }
                        }
                    }
                }
            }
        }
    }

    // Chest/belly details
    translate([0, 0, body_height/2 - 10]) {
        scale([0.9, 0.7, 0.5]) {
            sphere(d=body_length);
        }
    }

    // Back pattern details
    for(i = [1:5]) {
        translate([-body_length/2 + i*8, 0, body_height/2 + 5]) {
            scale([0.8, 0.6, 0.3]) {
                sphere(d=6);
            }
        }
    }
}

// Generate the pitta
pitta();

