// Rockfish 3D Model - Realistic Medium Complexity
// Designed for 3D printing with proper wall thickness and minimal overhangs

$fn = 32;  // Higher resolution for smoother curves

// Main parameters
body_length = 60;
body_height = 35;
body_width = 25;
fin_thickness = 1.5;
scale_factor = 0.8;

// Main body module
module rockfish_body() {
    // Main body - ellipsoid shape
    scale([1, 0.6, 0.4])
    sphere(d=body_length);

    // Head extension
    translate([body_length/2 - 5, 0, 0])
    scale([1.2, 0.9, 0.8])
    sphere(d=body_length/2);

    // Tail narrowing
    translate([-body_length/2 + 5, 0, 0])
    scale([0.6, 0.7, 0.5])
    sphere(d=body_length/2.5);
}

// Dorsal fin with spines
module dorsal_fin() {
    // Main fin base
    translate([0, 0, body_height/2 - 3])
    rotate([90, 0, 0])
    linear_extrude(height=fin_thickness)
    polygon(points=[
        [-body_length/3, 0],
        [-body_length/6, body_height/2],
        [0, body_height/3*1.2],
        [body_length/6, body_height/2],
        [body_length/3, 0]
    ]);

    // Fin spines
    for(i=[-body_length/3:body_length/6:body_length/3]) {
        translate([i, 0, body_height/2 - 2 + abs(i)/3])
        rotate([90, 0, 0])
        cylinder(h=body_height/3, r1=0.5, r2=0.2);
    }
}

// Pectoral fins
module pectoral_fins() {
    // Left fin
    translate([body_length/4, body_width/2, 0])
    rotate([0, 90, -30])
    scale([1.5, 1, 0.5])
    cylinder(h=body_length/4, r1=body_width/3, r2=body_width/5);

    // Right fin
    translate([body_length/4, -body_width/2, 0])
    rotate([0, 90, 30])
    scale([1.5, 1, 0.5])
    cylinder(h=body_length/4, r1=body_width/3, r2=body_width/5);
}

// Tail fin
module tail_fin() {
    // Upper lobe
    translate([-body_length/2 - 5, 0, body_height/4])
    rotate([0, 0, -20])
    scale([1, 2, 0.5])
    cylinder(h=body_height/2, r1=body_width/3, r2=0.5);

    // Lower lobe
    translate([-body_length/2 - 5, 0, -body_height/4])
    rotate([0, 0, 20])
    scale([1, 2, 0.5])
    cylinder(h=body_height/2, r1=body_width/3, r2=0.5);
}

// Mouth detail
module mouth() {
    translate([body_length/2, 0, -body_height/8])
    rotate([0, 90, 0])
    cylinder(h=body_length/8, r1=body_height/10, r2=body_height/15);
}

// Eyes
module eyes() {
    // Left eye
    translate([body_length/2 - 3, body_width/3, body_height/8])
    sphere(d=body_height/8);

    // Right eye
    translate([body_length/2 - 3, -body_width/3, body_height/8])
    sphere(d=body_height/8);

    // Pupils
    color("black")
    translate([body_length/2 - 2.5, body_width/3, body_height/8])
    sphere(d=body_height/16);

    color("black")
    translate([body_length/2 - 2.5, -body_width/3, body_height/8])
    sphere(d=body_height/16);
}

// Body pattern spots
module body_pattern() {
    for(i=[-body_length/2:body_length/8:body_length/2]) {
        for(j=[-body_width/3:body_width/6:body_width/3]) {
            if((i+j)%10 < 5) {
                translate([i, j, body_height/4])
                sphere(d=body_length/20);
            }
        }
    }
}

