body_height = 60;
total_height = 90;

module rook() {
    // Base - wide circular platform
    cylinder(h=8, d=base_diameter, center=false);

    // Tapered body
    translate([0, 0, 8])
    cylinder(h=body_height, d1=base_diameter, d2=base_diameter*0.7);

    // Corbelled parapet - alternating blocks
    translate([0, 0, 8+body_height])
    parapet();

    // Turret top
    translate([0, 0, 8+body_height+12])
    turret_top();

    // Battlements
    translate([0, 0, 8+body_height+12+8])
    battlements();
}

module parapet() {
    difference() {
        // Main parapet ring
        cylinder(h=12, d=base_diameter*0.7);

        // Inner hollow
        translate([0, 0, -1])
        cylinder(h=14, d=base_diameter*0.5);

        // Crenellations - 4 sides
        for (i=[0:45:315]) {
