scale_factor = 0.7;

// Body
module body() {
    // Main torso
    translate([0, 0, 8])
        scale([1.2, 0.7, 0.8])
        sphere(r = 15);

    // Belly
    translate([0, 0, 2])
        scale([1.1, 0.6, 0.4])
        sphere(r = 14);
}

// Head
module head() {
    // Main head shape
    translate([-20, 0, 15])
        scale([0.9, 0.7, 0.7])
        sphere(r = 10);

    // Snout
    translate([-32, 0, 12])
        scale([0.6, 0.4, 0.4])
        sphere(r = 8);

    // Nose
    color("black")
    translate([-38, 0, 11])
        sphere(r = 2);
}

// Ears
module ears() {
    // Left ear
    translate([-18, 8, 22])
        rotate([0, -10, -30])
        scale([0.3, 0.1, 0.6])
        cylinder(h = 10, r = 5);

    // Right ear
    translate([-18, -8, 22])
        rotate([0, -10, 30])
        scale([0.3, 0.1, 0.6])
        cylinder(h = 10, r = 5);
}

// Legs
module legs() {
    // Front legs
    for (y = [-7, 7]) {
        translate([-10, y, 5])
            rotate([0, 5, 0])
            scale([0.3, 0.3, 1])
            cylinder(h = 12, r = 4);
    }

    // Back legs
    for (y = [-7, 7]) {
        translate([10, y, 5])
            rotate([0, -5, 0])
            scale([0.3, 0.3, 1.2])
            cylinder(h = 14, r = 4.5);
    }
}

// Tail
module tail() {
    // Tail base
    translate([18, 0, 10])
        rotate([0, 0, -20])
        scale([0.4, 0.4, 2])
        cylinder(h = 20, r = 4);

    // Tail tip
    translate([32, -8, 18])
        rotate([0, 0, -45])
        scale([0.3, 0.3, 1])
        sphere(r = 5);
}

// Eyes
module eyes() {
    color("black")
    for (y = [-4, 4]) {
        translate([-28, y, 15])
            sphere(r = 1.5);
    }
}

// Whiskers
module whiskers() {
    for (i = [0:2]) {
        angle = i * 30 - 30;
        // Left side
        translate([-35, 5, 11])
            rotate([0, 0, angle])
            cylinder(h = 8, r = 0.5);

        // Right side
        translate([-35, -5, 11])
            rotate([0, 0, -angle])
            cylinder(h = 8, r = 0.5);
    }
}

// Combine all parts
scale([scale_factor, scale_factor, scale_factor])
union() {
    body();
    head();
    ears();
    legs();
    tail();
    eyes();
    whiskers();
}

