// OpenSCAD Sardine – realistic, medium-complexity, 3D-printable
// Overall length ≈ 80 mm
// Wall thickness 1.2 mm

module sardine() {
    // Body core – two fused ellipsoids for realistic fish shape
    translate([0,0,0])
        scale([1,0.38,0.25])
        sphere(d=30,$fn=32);

    translate([20,0,0])
        scale([1.3,0.32,0.22])
        sphere(d=30,$fn=32);

    // Tail fin – thin flat wedge
    difference() {
        translate([38,0,0])
            rotate([0,0,-10])
            scale([1,0.6,0.05])
            cylinder(h=20,d1=0,d2=18,$fn=4);
        translate([40,0,0])
            cube([25,20,1],center=true);
    }

    // Dorsal fin – two segments
    for(i=[-5,5])
    translate([i,0,6])
        rotate([90,0,0])
        linear_extrude(height=1.2)
        polygon(points=[[0,0],[4,6],[-4,6]]);

    // Pectoral fins (left & right)
    for(side=[-1,1])
    translate([5,side*8, -2])
        rotate([0,side*15,0])
        scale([1,0.3,0.05])
        cylinder(h=12,d1=8,d2=0,$fn=3);

    // Lower jaw / mouth
    difference() {
        translate([-12,0,-2])
            scale([1.2,0.5,0.3])
            sphere(d=12,$fn=32);
        translate([-12,0,-2.5])
            cube([15,8,2],center=true);
    }

    // Eye sockets
    for(side=[-1,1])
    translate([-14,side*4,1])
        rotate([0,90,0])
        cylinder(h=3,d=4,$fn=16);

    // Eyes
    color("white")
    for(side=[-1,1])
    translate([-15.2,side*4,1])
        sphere(d=2.5,$fn=16);

    color("black")
    for(side=[-1,1])
    translate([-16.2,side*4,1])
        sphere(d=1.2,$fn=16);

    // Scales pattern – repeated raised disks on upper body
    for(x=[-10:5:25])
    for(y=[-6:3:6])
    translate([x,y,6.5])
        sphere(d=1.2,$fn=8);
}

// Main model – centered upright for easy printing
rotate([0,-90,0])
    sardine();

