// Sapsucker Woodpecker - Realistic Medium Complexity
// Overall size ~70mm tall

// Parameters
body_length = 35;
body_height = 20;
head_size = 12;
beak_length = 8;
beak_width = 2;
wing_span = 30;
tail_length = 15;
leg_length = 8;
foot_size = 4;

// Colors (for visualization)
color_body = "red";
color_wing = "black";
color_head = "red";
color_beak = "black";

module body() {
    // Main body - elongated sphere
    translate([0, 0, leg_length + body_height/2])
    scale([1.8, 1, 1])
    sphere(d=body_height, $fn=32);

    // Back pattern - black and white stripes
    for(i = [-2:1:2]) {
        translate([i*3, body_height/2-2, leg_length + body_height/2])
        scale([2, 0.5, 0.1])
        cube([4, 4, 1], center=true);
    }
}

module head() {
    // Head sphere
    translate([body_length/2 + head_size/2, 0, leg_length + body_height/2 + head_size/2])
    sphere(d=head_size, $fn=32);

    // Red crest patch
    translate([body_length/2 + head_size/2, 0, leg_length + body_height/2 + head_size/2 + 2])
    rotate([0, 90, 0])
    cylinder(d1=6, d2=4, h=3, $fn=16);

    // Eye
    translate([body_length/2 + head_size/2 + 2, head_size/3, leg_length + body_height/2 + head_size/2])
    sphere(d=2.5, $fn=16);

    // White eye ring
    difference() {
        translate([body_length/2 + head_size/2 + 2, head_size/3, leg_length + body_height/2 + head_size/2])
        sphere(d=3, $fn=16);

        translate([body_length/2 + head_size/2 + 2.5, head_size/3, leg_length + body_height/2 + head_size/2])
        sphere(d=2.5, $fn=16);
    }
}

module beak() {
    // Main beak
    translate([body_length/2 + head_size + beak_length/2, 0, leg_length + body_height/2 + head_size/2 - 2])
    rotate([0, -10, 0])
    scale([1, 0.7, 0.7])
    cylinder(d1=beak_width, d2=beak_width*0.5, h=beak_length, $fn=16);

    // Lower mandible
    translate([body_length/2 + head_size + beak_length/2 - 1, 0, leg_length + body_height/2 + head_size/2 - 3])
    rotate([0, -10, 0])
    scale([0.8, 0.5, 0.4])
    cylinder(d1=beak_width, d2=beak_width*0.5, h=beak_length, $fn=16);
}

module wing() {
    // Left wing
    translate([0, -body_height/2 - 1, leg_length + body_height/2 + 5])
    rotate([0, 0, -20])
    scale([1.5, 0.3, 1])
    difference() {
        // Main wing shape
        translate([-wing_span/4, 0, 0])
        cube([wing_span/2, 15, 8]);

        // Wing tip cutout
        translate([wing_span/4 - 2, 5, 0])
        rotate([0, 0, 30])
        cube([5, 10, 10]);

        // Feather details
        for(i = [0:2:8]) {
            translate([-wing_span/4 + i, 0, 2])
            cube([1, 15, 6]);
        }
    }

    // Right wing (mirrored)
    mirror([0, 1, 0])
    translate([0, body_height/2 + 1, leg_length + body_height/2 + 5])
    rotate([0, 0, 20])
    scale([1.5, 0.3, 1])
    difference() {
        translate([-wing_span/4, 0, 0])
        cube([wing_span/2, 15, 8]);

        translate([wing_span/4 - 2, 5, 0])
        rotate([0, 0, 30])
        cube([5, 10, 10]);

        for(i = [0:2:8]) {
            translate([-wing_span/4 + i, 0, 2])
            cube([1, 15, 6]);
        }
    }
}

module tail() {
    // Tail feathers
    translate([-body_length/2 - tail_length/2, 0, leg_length + body_height/2 - 5])
    difference() {
        // Main tail
        scale([1, 0.3, 1])
        cube([tail_length, 12, 8]);

        // Tail notch
        translate([0, 0, 4])
        rotate([0, 90, 0])
        cylinder(d=4, h=10, $fn=16);
    }

    // Individual tail feathers
    for(i = [-1.5:1:1.5]) {
        translate([-body_length/2 - tail_length - 1, i*2, leg_length + body_height/2 - 5])
        rotate([0, 0, i*5])
        scale([0.2, 1, 0.1])
        cube([tail_length/2, 2, 8]);
    }
}

module legs() {
    // Left leg
    translate([body_length/4, -body_height/4, leg_length/2])
    cylinder(d=2, h=leg_length, $fn=16);

    // Left foot
    translate([body_length/4 + foot_size/2, -body_height/4 - foot_size/2, 1])
    rotate([90, 0, 0])
    cylinder(d=foot_size, h=1, $fn=8);

    // Toes
    for(i = [-1:1:1]) {
        translate([body_length/4 + foot_size/2 + i*1.5, -body_height/4 - foot_size, 1])
        rotate([90, 0, 0])
        cylinder(d1=1, d2=0.5, h=3, $fn=8);
    }

    // Right leg
    translate([body_length/4, body_height/4, leg_length/2])
    cylinder(d=2, h=leg_length, $fn=16);

    // Right foot
    translate([body_length/4 + foot_size/2, body_height/4 + foot_size/2, 1])
    rotate([90, 0, 0])
    cylinder(d=foot_size, h=1, $fn=8);

    // Toes
    for(i = [-1:1:1]) {
        translate([body_length/4 + foot_size/2 + i*1.5, body_height/4 + foot_size, 1])
        rotate([90, 0, 0])
        cylinder(d1=1, d2=0.5, h=3, $fn=8);
    }
}

// Assembly
union() {
    body();
    head();
    beak();
    wing();
    tail();
    legs();
}

