// Sawfish in realistic medium complexity
// 3D printable without overhangs

// Overall sizing
$fn = 32;
length = 80;
width = 25;
height = 8;

module sawfish() {
    difference() {
        union() {
            // Main body
            translate([0, 0, 0]) 
                scale([1, 0.5, 0.3]) 
                    sphere(d = width);

            // Tail extension
            translate([-length/2 - 5, 0, 0]) 
                rotate([0, 0, -15]) 
                    scale([1.5, 0.3, 0.25]) 
                        cylinder(h = length/2, d = width);

            // Head to rostrum transition
            hull() {
                translate([length/2 - 5, 0, 0]) 
                    scale([1, 0.5, 0.3]) 
                        sphere(d = width*0.9);
                translate([length/2 + 15, 0, 0]) 
                    scale([1, 0.3, 0.2]) 
                        sphere(d = width*0.4);
            }

            // Rostrum (saw)
            translate([length/2 + 30, 0, 0]) 
                rotate([0, 0, 0]) 
                    scale([4, 0.05, 0.05]) 
                        cube([length/2, width, height], center = true);

            // Dorsal fin
            translate([-10, 0, height/2 + 2]) 
                rotate([30, 0, 0]) 
                    scale([0.5, 0.8, 1]) 
                        cylinder(h = height*1.5, d = width/3);

            // Pectoral fins
            for (y = [-1, 1]) {
                translate([0, y*width/2, -height/2]) 
                    rotate([0, y*30, 0]) 
                        scale([1, 0.5, 0.1]) 
                            cylinder(h = width/1.5, d = width/2);
            }

            // Pelvic fins
            for (y = [-1, 1]) {
                translate([-length/3, y*width/2, -height/2]) 
                    rotate([0, y*45, 0]) 
                        scale([0.8, 0.4, 0.1]) 
                            cylinder(h = width/2, d = width/3);
            }
        }

        // Eye sockets
        for (x = [1, -1]) {
            translate([length/2 - 5, x*width/4, height/4]) 
                sphere(d = width/6);
        }

        // Mouth
        translate([length/2 - 8, 0, -height/4]) 
            cube([width/2, width/4, height/2], center = true);

        // Gill slits
        for (i = [0:4]) {
            translate([length/2 - 12 - i*3, 0, -height/8]) 
                cube([2, width/2.5, height/4], center = true);
        }
    }

    // Rostral teeth
    for (i = [0:15]) {
        tooth_x = length/2 + 35 + i*2.5;
        tooth_y = (i % 2 == 0) ? width/8 : -width/8;
        translate([tooth_x, tooth_y, 0]) 
            rotate([0, 90, 0]) 
                cylinder(h = height/4, d1 = 1.5, d2 = 0.5);
    }

    // Eyeballs
    for (x = [1, -1]) {
        translate([length/2 - 5, x*width/4, height/4]) 
            sphere(d = width/8);
    }
}

// Generate the sawfish
sawfish();

