// Scaup Duck (Medium Complexity)
// Realistic style, 3D printable

$fn = 24;

module scaup() {
    // Body - main ellipsoid shape
    union() {
        // Main body
        scale([1.2, 0.8, 0.9])
            sphere(20);

        // Tail extension
        translate([-22, 0, -5])
            rotate([0, 20, 0])
            scale([1.5, 0.8, 0.3])
            cylinder(8, 8, 2);

        // Chest bulge
        translate([15, 0, -15])
            scale([0.9, 1.2, 0.8])
            sphere(10);
    }

    // Head
    translate([22, 0, 8])
        union() {
            // Head sphere
            scale([0.8, 0.9, 0.85])
                sphere(12);

            // Bill
            translate([8, 0, -2])
                rotate([0, -10, 0])
                scale([1.8, 0.4, 0.3])
                cylinder(12, 5, 3);

            // Bill nail
            translate([19, 0, -2])
                scale([0.4, 0.8, 0.8])
                sphere(2.5);

            // Eye
            translate([5, 6, 3])
                sphere(2);

            // Eye highlight
            translate([5.5, 6.5, 3.5])
                scale([0.5, 0.5, 0.5])
                sphere(1);
    }

    // Neck
    hull() {
        translate([15, 0, 0])
            sphere(5);
        translate([22, 0, 8])
            sphere(5);
    }

    // Wings
    module wing() {
        difference() {
            // Main wing
            translate([0, 0, 0])
                rotate([0, 0, -15])
                scale([1.2, 0.3, 0.8])
                cylinder(25, 12, 8);

            // Wing tip
            translate([20, 0, 0])
                rotate([0, 0, -15])
                scale([0.8, 0.3, 0.6])
                cylinder(15, 8, 0);

            // Feather indentations
            for(i = [1:3]) {
                translate([-5 + i*8, 0, i*2])
                    rotate([0, 0, -15])
                    scale([0.1, 0.4, 0.4])
                    cylinder(25, 10, 6);
            }
        }
    }

    // Left wing
    translate([0, 15, 5])
        rotate([0, -20, 20])
        wing();

    // Right wing
    translate([0, -15, 5])
        rotate([0, -20, -20])
        mirror([0,1,0])
        wing();

    // Tail feathers
    module tail_feather() {
        rotate([0, 40, 0])
        scale([0.1, 0.8, 0.4])
        cylinder(15, 3, 0);
    }

    for(i = [-2:1:2]) {
        translate([-25, i*3, i*2-2])
            rotate([0, 0, i*5])
            tail_feather();
    }

    // Feet
    module foot() {
        // Main foot
