        // Main thorax shell - ellipsoid shape
        scale([1.2, 1, 0.8]) 
        sphere(r = 12);

        // Head
        translate([15, 0, 5])
        scale([1.5, 1.2, 1])
        sphere(r = 7);

        // Extended pronotum (neck area)
        translate([8, 0, -2])
        scale([1.8, 1.4, 0.6])
        sphere(r = 6);
    }
}

// Elytra (wing covers) with realistic segmentation
module elytra() {
    difference() {
        // Main elytra shell
        translate([0, 0, 10])
        scale([1.8, 2.2, 0.6])
        sphere(r = 12);

        // Division line between elytra
        translate([-8, 0, 9])
        cube([16, 1, 4]);

        // Trim excess
        translate([0, 0, 16])
        cube([40, 40, 10], center = true);
    }

    // Add elytra texture lines
    for(i = [1:6]) {
        translate([-20 + i*7, -15, 10])
        rotate([0, 0, 5])
        scale([1, 0.1, 0.05])
        sphere(r = 12);
    }
}

// Leg segments with joints
module leg(side) {
    // Side modifier -1 for left, 1 for right
    mirror([0, side, 0]) {
        // Coxa (base leg segment)
        translate([8, side*10, 0])
        rotate([0, 0, -30])
        cylinder(h = 8, r1 = 3, r2 = 2.5);

        // Trochanter
        translate([12, side*12, -2])
        rotate([0, 0, 45])
        cylinder(h = 6, r1 = 2.5, r2 = 2);

        // Femur
        translate([14, side*14, -5])
        rotate([0, 0, 60])
        scale([1, 1, 0.8])
        cylinder(h = 10, r1 = 2, r2 = 1.8);

        // Tibia
        translate([17, side*17, -9])
        rotate([0, 0, 75])
        cylinder(h = 8, r1 = 1.8, r2 = 1.5);

        // Tarsus (foot)
        translate([19, side*19, -12])
        rotate([0, 0, 80])
        scale([1, 1, 0.6])
        cylinder(h = 6, r1 = 1.5, r2 = 1);
