// Schipperke dog model - medium complexity
// Using only OpenSCAD primitives and proper 3D printing guidelines

// Overall scale factor for 3D printing
scale_factor = 0.8;

// Body dimensions
body_length = 45 * scale_factor;
body_width = 25 * scale_factor;
body_height = 30 * scale_factor;

// Head dimensions
head_length = 20 * scale_factor;
head_width = 18 * scale_factor;
head_height = 22 * scale_factor;

// Leg dimensions
leg_height = 12 * scale_factor;
leg_width = 5 * scale_factor;

// Tail dimensions
tail_length = 15 * scale_factor;
tail_diameter = 6 * scale_factor;

module schipperke() {
    // Main body - slightly tapered from chest to rear
    translate([0, 0, leg_height + body_height/2]) {
        hull() {
            // Front of body (wider)
            translate([-body_length/3, 0, 0]) {
                scale([1.2, 1, 1]) sphere(d=body_width, $fn=20);
            }
            // Rear of body (narrower)
            translate([body_length/3, 0, 0]) {
                scale([0.8, 0.9, 0.9]) sphere(d=body_width, $fn=20);
            }
        }
    }

    // Chest area - more pronounced
    translate([-body_length/2.5, 0, leg_height + body_height/3]) {
        sphere(d=body_width * 1.3, $fn=20);
    }

    // Head
    translate([-body_length/2 - head_length/2, 0, leg_height + body_height * 0.8]) {
        // Main head shape
        translate([0, 0, 0]) {
            scale([1.3, 1, 1.1]) sphere(d=head_width, $fn=20);
        }

        // Snout/muzzle
        translate([-head_length/2, 0, -head_height/4]) {
            scale([1.5, 0.7, 0.6]) sphere(d=head_width * 0.8, $fn=20);
        }

        // Ears - pointed and upright
        for(side = [-1, 1]) {
            translate([head_length/4, side * head_width/2, head_height/3]) {
                rotate([0, 0, side * 20]) {
                    scale([0.3, 0.1, 1]) cylinder(h=head_height * 0.8, d=head_width * 0.5, $fn=3);
                }
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([-head_length/4, side * head_width/3, head_height/6]) {
                sphere(d=head_width/6, $fn=10);
            }
        }

        // Nose
        translate([-head_length/2 - 1, 0, -head_height/4]) {
            sphere(d=head_width/5, $fn=10);
        }
    }

    // Legs - simplified cylinders with paws
    leg_positions = [
        [-body_length/3, body_width/2.5, 0],  // front right
        [-body_length/3, -body_width/2.5, 0], // front left
        [body_length/3, body_width/3, 0],     // rear right
        [body_length/3, -body_width/3, 0]     // rear left
    ];

    for(pos = leg_positions) {
        translate(pos) {
            // Upper leg
            cylinder(h=leg_height * 0.7, d=leg_width, $fn=12);

            // Lower leg
            translate([0, 0, leg_height * 0.7]) {
                cylinder(h=leg_height * 0.3, d=leg_width * 0.8, $fn=12);
            }

            // Paw
            translate([0, 0, leg_height]) {
                sphere(d=leg_width * 1.2, $fn=12);
            }
        }
    }

    // Tail - curled up over back
    translate([body_length/2, 0, leg_height + body_height * 0.7]) {
        rotate([0, 90, 15]) {
            for(i = [0:15:180]) {
                rotate([0, 0, i]) {
                    translate([tail_length * sin(i) * 0.8, 0, tail_length * (1 - cos(i)) * 0.4]) {
                        sphere(d=tail_diameter * (1 - i/360), $fn=12);
                    }
                }
            }
        }
    }

    // Fur texture - simple bumps along back and flanks
    fur_positions = [
        for(i = [-body_length/2:5:body_length/2])
        for(j = [-body_width/2:4:body_width/2])
        [i, j, leg_height + body_height + 2]
    ];

    for(pos = fur_positions) {
        if(norm([pos.x, pos.y]) < body_width/1.8) {
            translate(pos) {
                sphere(d=2 * scale_factor, $fn=8);
            }
        }
    }
}

