// Realistic shad (Alosa sapidissima) - medium complexity
// Total length ~80 mm, designed for FDM printing (0.4 mm nozzle)

shad_length = 80;           // overall fish length
wall_th = 1.2;              // 3 perimeters @ 0.4 mm

module body_shape() {
    // Main fuselage of the fish
    difference() {
        hull() {
            // Tapered ellipsoid made from stretched spheres
            scale([1, 0.45, 0.25]) sphere(d = shad_length, $fn = 60);
            translate([shad_length*0.6, 0, 0])
                scale([0.7, 0.35, 0.2]) sphere(d = shad_length*0.7, $fn = 50);
        }
        // Hollow out interior
        scale([0.85, 0.35, 0.18]) sphere(d = shad_length, $fn = 60);
    }
}

module head_details() {
    // Snout protrusion
    translate([shad_length/2-2, 0, 0])
        rotate([0, -10, 0])
        scale([1, 0.6, 0.4])
        cylinder(h = 6, d1 = 8, d2 = 4, $fn = 20);

    // Eye sockets (left and right)
    for (side = [-1, 1]) {
        translate([shad_length*0.35, side*6, 1.5])
            rotate([0, 90, 0])
            difference() {
                cylinder(h = 4, d = 5, $fn = 20);
                translate([-1, 0, 0])
                    cylinder(h = 5, d = 2.4, $fn = 16);
            }
    }
}

module tail_fin() {
    // Tail fork (heterocercal)
    translate([-shad_length/2-0.5, 0, 0])
    union() {
        // Upper lobe
        rotate([0, 0, 25])
            translate([0, 0, 0])
            scale([1, 0.3, 0.05])
            cube([12, 20, wall_th*2], center = true);

        // Lower lobe
        rotate([0, 0, -30])
            translate([0, 0, 0])
            scale([1, 0.25, 0.05])
            cube([10, 18, wall_th*2], center = true);
    }
}

module dorsal_fin() {
    // Positioned mid-body
    fin_base = -shad_length*0.1;
    for (i = [0:5]) {
        translate([fin_base - i*2.5, 0, 3+i*0.3])
            rotate([0, 90-i*2, 0])
            scale([0.6, 0.3, 0.05])
            cylinder(h = 8-i*0.5, d1 = 1.2, d2 = 0.4, $fn = 6);
    }
}

module pectoral_fin(side) {
    // Side fins just behind head
    translate([shad_length*0.25, side*shad_length*0.22, -1])
        rotate([0, 20*side, 45*side])
        scale([1, 0.35, 0.05])
        cube([12, 8, wall_th*2], center = true);
}

module pelvic_fin(side) {
    // Ventral fins
    translate([-shad_length*0.1, side*shad_length*0.15, -2])
        rotate([0, 30, 15*side])
        scale([0.8, 0.3, 0.05])
        cube([8, 6, wall_th*2], center = true);
}

module anal_fin() {
    // Rear ventral fin
    fin_start = -shad_length*0.35;
    for (i = [0:4]) {
        translate([fin_start - i*2, 0, -2.5-i*0.2])
            rotate([0, -90+i*2, 0])
            scale([0.5, 0.25, 0.05])
            cylinder(h = 6-i*0.3, d1 = 1, d2 = 0.4, $fn = 6);
    }
}

// Assembly
union() {
    body_shape();
    head_details();
    tail_fin();
    dorsal_fin();
    anal_fin();
    for (s = [-1, 1]) {
        pectoral_fin(s);
        pelvic_fin(s);
    }
}

