// Realistic Shiner (3D printable)

// Overall scale factor
scale_factor = 0.4;

// Body parameters
body_length = 120 * scale_factor;
body_diameter = 35 * scale_factor;
head_diameter = 40 * scale_factor;
tail_width = 50 * scale_factor;
tail_height = 30 * scale_factor;

// Fin parameters
dorsal_fin_height = 25 * scale_factor;
dorsal_fin_length = 60 * scale_factor;
pectoral_fin_length = 20 * scale_factor;
pectoral_fin_height = 15 * scale_factor;

// Eye parameters
eye_diameter = 8 * scale_factor;
eye_offset = 15 * scale_factor;

// Mouth parameters
mouth_width = 15 * scale_factor;
mouth_height = 5 * scale_factor;

// Scale thickness
wall_thickness = 2 * scale_factor;

// Main body module
module fish_body() {
    // Create main body shape using hull of spheres
    hull() {
        translate([-body_length/2, 0, 0])
            sphere(d=body_diameter, $fn=20);
        translate([body_length/2, 0, 0])
            sphere(d=head_diameter, $fn=20);
    }

    // Add tail
    difference() {
        translate([body_length/2, 0, 0])
            cube([tail_width, tail_height, tail_height], center=true);

        // Carve tail shape
        translate([body_length/2 + tail_width/2 + 5, 0, 0])
            rotate([0, 0, 0])
            cylinder(h=tail_height + 10, r=tail_height/2, $fn=3);
    }
}

// Dorsal fin
module dorsal_fin() {
    difference() {
        // Main fin
        translate([0, 0, body_diameter/2])
            linear_extrude(height=dorsal_fin_length)
                polygon(points=[
                    [0, 0],
                    [-dorsal_fin_length/2, dorsal_fin_height],
                    [dorsal_fin_length/2, dorsal_fin_height]
                ]);

        // Add fin rays
        for(i = [-3:3]) {
            translate([i * 10 * scale_factor, 0, body_diameter/2 + dorsal_fin_height/2])
                cube([1, dorsal_fin_height, 1]);
        }
    }
}

// Pectoral fin
module pectoral_fin(side) {
    mirror([side, 0, 0]) {
        translate([0, body_diameter/2, -body_diameter/4])
            rotate([0, -30, 0])
            difference() {
                // Fin shape
                scale([1, 0.7, 0.2])
                    sphere(d=pectoral_fin_length, $fn=20);

                // Carve fin
                translate([pectoral_fin_length/2, 0, 0])
                    rotate([0, 45, 0])
                    cube(pectoral_fin_length, center=true);
            }
    }
}

// Eye module
module eye(side) {
    translate([eye_offset, side * body_diameter/3, body_diameter/3]) {
        // Eye socket
        difference() {
            sphere(d=eye_diameter + 2);
            sphere(d=eye_diameter);
        }

        // Eye ball
        translate([0, 0, 1])
            sphere(d=eye_diameter - 1);

        // Pupil
        translate([0, 0, eye_diameter/2])
            sphere(d=eye_diameter/3);
    }
}

// Mouth
module mouth() {
    translate([body_length/2 - 5, 0, -body_diameter/4])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=mouth_height, r=mouth_width/2, $fn=20);
            translate([0, 0, -1])
                cube([mouth_width + 2, mouth_height + 2, mouth_height + 2], center=true);
        }
}

// Gills
module gills() {
    for(i = [1:3]) {
        translate([body_length/4 - i * 5 * scale_factor, body_diameter/2 - 2, 0])
            rotate([0, 90, 0])
            cylinder(h=wall_thickness, r=3 * scale_factor);
    }
}

// Final assembly
union() {
    fish_body();
    dorsal_fin();
    pectoral_fin(1);
    pectoral_fin(-1);
    eye(1);
    eye(-1);
    mouth();
    gills();
}

