module shoveler() {
    // Body parameters
    body_length = 50;
    body_height = 25;
    body_width = 22;

    // Head parameters
    head_length = 20;
    head_height = 18;
    head_width = 15;

    // Bill parameters
    bill_length = 35;
    bill_width = 20;
    bill_height = 8;

    // Leg parameters
    leg_height = 15;
    leg_width = 4;
    foot_length = 12;
    foot_width = 8;

    // Tail parameters
    tail_length = 12;
    tail_width = 18;

    // Body - main oval shape
    union() {
        // Main body
        translate([0, 0, body_height/2])
            scale([1, 0.8, 0.6])
                sphere(d=body_height);

        // Body extension for back
        translate([-body_length/2 - 5, 0, body_height/2 - 5])
            cube([10, body_width, 10], center=true);

        // Chest bulge
        translate([body_length/2 - 10, 0, body_height/2 - 8])
            scale([1.2, 1, 0.8])
                sphere(d=body_height * 0.7);
    }

    // Head
    translate([body_length/2 + head_length/2 - 5, 0, body_height/2 + 5])
        rotate([0, 20, 0])
            union() {
                // Main head
                scale([1.3, 1, 1])
                    sphere(d=head_height);

                // Eye
                translate([head_length/2 - 5, head_width/3, head_height/4])
                    sphere(d=3);

                // Eye ring
                translate([head_length/2 - 5, head_width/3, head_height/4])
                    sphere(d=3.5);
            }

    // Shovel-shaped bill
    translate([body_length/2 + head_length + bill_length/2 - 5, 0, body_height/2 + 2])
        rotate([0, 20, 0])
            union() {
                // Upper bill
                translate([0, 0, 2])
                    scale([1, 0.9, 0.5])
                        cube([bill_length, bill_width, bill_height], center=true);

                // Lower bill
                translate([0, 0, -2])
                    scale([1, 0.9, 0.4])
                        cube([bill_length * 0.9, bill_width * 0.95, bill_height * 0.8], center=true);

                // Bill ridge
                for(i = [0:3]) {
                    translate([-bill_length/2 + i * 8, 0, bill_height/2 + 1])
                        scale([0.3, 1, 0.3])
                            sphere(d=bill_width * 0.8);
                }
            }

    // Wings
    difference() {
        // Left wing
        translate([-5, body_width/2 + 2, body_height/2 + 5])
            rotate([0, 0, -20])
                scale([1.5, 0.3, 1])
                    sphere(d=body_height * 1.2);

        // Wing detail lines
        for(i = [1:3]) {
            translate([-5, body_width/2 + 2 + i * 2, body_height/2 + 5])
                rotate([0, 0, -20])
                    scale([1.5, 0.05, 1])
                        sphere(d=body_height * 1.1);
        }
    }

    // Right wing (mirrored)
    mirror([0, 1, 0]) {
        translate([-5, body_width/2 + 2, body_height/2 + 5])
            rotate([0, 0, -20])
                scale([1.5, 0.3, 1])
                    sphere(d=body_height * 1.2);
    }

    // Tail
    translate([-body_length/2 - tail_length/2, 0, body_height/2 - 5])
        rotate([10, 0, 0])
            scale([1, 1, 0.3])
                cube([tail_length, tail_width, 8], center=true);

    // Legs and feet
    // Left leg
    translate([0, body_width/2 - 3, -leg_height/2])
        union() {
            // Leg
            cylinder(h=leg_height, d=leg_width);

            // Foot
            translate([0, foot_length/2, -leg_height/2])
                rotate([0, 20, 0])
                    union() {
                        // Main foot
                        scale([1.5, 1, 0.3])
                            sphere(d=foot_width);

                        // Toes
                        for(i = [-1:1]) {
                            translate([i * 3, foot_length/2 + 2, 0])
                                rotate([0, 0, i * 15])
